import React, { useState, useEffect } from "react";
import { Container, Button, Form, Spinner, Card, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { jsPDF } from "jspdf"; 
import { Chart } from "react-google-charts";

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({ firstName: "", lastName: "", skills: "", educationLevel: "", degree: "", experience: "", jobRole: "" });
  const [loading, setLoading] = useState(false);
  const [skillGap, setSkillGap] = useState([]);
  const [roadmap, setRoadmap] = useState([]);
  const [learningResources, setLearningResources] = useState([]);

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");

    if (!token || !userData) {
      alert("Please log in first!");
      navigate("/");
    }

    setUser(userData);
    setFormData({ firstName: userData.firstName, lastName: userData.lastName });
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setSkillGap([]);
    setRoadmap([]);
    setLearningResources([]);

    try {
      const response = await axios.post(
        "http://localhost:5000/career/career-recommendation",
        formData,
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );

      setSkillGap(response.data.skillGap);
      setRoadmap(response.data.roadmap);
      setLearningResources(response.data.learningResources);
    } catch (error) {
      console.error("Error:", error);
    } finally {
      setLoading(false);
    }
  };

  const downloadPDF = () => {
    const doc = new jsPDF("p", "mm", "a4");
    doc.setFont("helvetica", "bold");
    doc.setFontSize(22);
    doc.text("PathToGrow", 14, 20);

    doc.setFontSize(18);
    doc.text("Career Recommendations", 14, 30);
    doc.setLineWidth(0.5);
    doc.line(14, 32, 200, 32);

    doc.setFont("helvetica", "normal");
    doc.setFontSize(12);
    let yOffset = 40;

    skillGap.forEach((item) => {
      doc.text(`Skill Gap: ${item}`, 14, yOffset);
      yOffset += 10;
    });

    roadmap.forEach((step) => {
      doc.text(`Roadmap Step: ${step}`, 14, yOffset);
      yOffset += 10;
    });

    learningResources.forEach((link) => {
      doc.text(`Resource: ${link}`, 14, yOffset);
      yOffset += 10;
    });

    doc.save("career_recommendations.pdf");
  };

  return (
    <Container>
      <Card className="shadow mb-5">
        <Card.Body>
          <h3 className="fw-bold">Welcome, {user?.firstName} {user?.lastName}!</h3>

          <Form onSubmit={handleSubmit}>
            <Row>
              <Col md={6}>
                <Form.Group controlId="skills">
                  <Form.Label>Your Skills</Form.Label>
                  <Form.Control type="text" name="skills" value={formData.skills} onChange={handleChange} />
                </Form.Group>

                <Form.Group controlId="educationLevel">
                  <Form.Label>Education Level</Form.Label>
                  <Form.Control type="text" name="educationLevel" value={formData.educationLevel} onChange={handleChange} />
                </Form.Group>
              </Col>

              <Col md={6}>
                <Form.Group controlId="jobRole">
                  <Form.Label>Desired Job Role</Form.Label>
                  <Form.Control type="text" name="jobRole" value={formData.jobRole} onChange={handleChange} />
                </Form.Group>

                <Button type="submit" disabled={loading} className="mt-3">
                  {loading ? <Spinner animation="border" size="sm" /> : "Analyze My Skills"}
                </Button>
              </Col>
            </Row>
          </Form>
        </Card.Body>
      </Card>

      {skillGap.length > 0 && (
        <Card className="shadow mb-5">
          <Card.Body>
            <h4 className="fw-bold">Skill Gap Analysis</h4>
            <Chart
              chartType="BarChart"
              data={[["Skill", "Proficiency"], ...skillGap.map((skill) => [skill, Math.random() * 100])]}
              width="100%"
              height="300px"
            />
          </Card.Body>
        </Card>
      )}

      {learningResources.length > 0 && (
        <Card className="shadow mb-5">
          <Card.Body>
            <h4 className="fw-bold">Learning Resources</h4>
            <ul>
              {learningResources.map((link, index) => (
                <li key={index}><a href={link} target="_blank" rel="noopener noreferrer">{link}</a></li>
              ))}
            </ul>
          </Card.Body>
        </Card>
      )}

      <Button variant="outline-primary" onClick={downloadPDF}>
        Download Report
      </Button>
    </Container>
  );
};

export default Dashboard;
