import React, { useState, useEffect } from "react";
import { Container, Button, Form, Spinner, Card, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { jsPDF } from "jspdf";
import { Chart } from "react-google-charts"; // Google Charts Import

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    skills: "",
    educationLevel: "",
    degree: "",
    experience: "",
    jobRole: ""
  });
  const [loading, setLoading] = useState(false);
  const [recommendations, setRecommendations] = useState("");
  const [skillGap, setSkillGap] = useState([]);
  const [learningResources, setLearningResources] = useState([]);

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");

    if (!token || !userData) {
      alert("Please log in first!");
      navigate("/");
    }

    setUser(userData);
    setFormData({
      firstName: userData.firstName,
      lastName: userData.lastName,
    });
  }, [navigate]);

  const handleProfileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setRecommendations("");
    setSkillGap([]);
    setLearningResources([]);

    try {
      const response = await axios.post(
        "http://localhost:5000/career/career-recommendation",
        formData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      
      const aiResponse = response.data.recommendations.split("\n");
      setRecommendations(aiResponse);
      
      const extractedSkillGap = aiResponse.filter(line => line.includes("Skill Gap:"));
      const extractedResources = aiResponse.filter(line => line.includes("Learning Resource:"));
      setSkillGap(extractedSkillGap);
      setLearningResources(extractedResources);
    } catch (error) {
      setRecommendations("Error fetching career recommendations.");
    } finally {
      setLoading(false);
    }
  };

  const downloadPDF = () => {
    const doc = new jsPDF("p", "mm", "a4");
    doc.setFont("helvetica", "bold");
    doc.setFontSize(22);
    doc.text("PathToGrow Career Report", 14, 20);
    doc.setFontSize(18);
    doc.text("Career Recommendations", 14, 30);
    doc.line(14, 32, 200, 32);
    
    let yOffset = 40;
    recommendations.forEach((line) => {
      doc.text(line, 14, yOffset);
      yOffset += 10;
    });
    
    doc.save("career_recommendations.pdf");
  };

  return (
    <Container className="py-5">
      <Row>
        <Col md={12} className="mb-3 text-end">
          <Button variant="danger" onClick={() => navigate("/")}>Logout</Button>
        </Col>
      </Row>
      
      {/* Skill Gap Analysis Chart */}
      {skillGap.length > 0 && (
        <Card className="mb-4">
          <Card.Header>Skill Gap Analysis</Card.Header>
          <Card.Body>
            <Chart
              width={'100%'}
              height={'400px'}
              chartType="BarChart"
              loader={<Spinner animation="border" />}
              data={[['Skill', 'Gap Level'], ...skillGap.map(skill => [skill.split(': ')[1], Math.random() * 100])]}
              options={{ title: 'Your Skill Gap', hAxis: { title: 'Gap Level' } }}
            />
          </Card.Body>
        </Card>
      )}

      {/* Learning Resources Section */}
      {learningResources.length > 0 && (
        <Card className="mb-4">
          <Card.Header>Learning Resources</Card.Header>
          <Card.Body>
            <ul>
              {learningResources.map((resource, index) => (
                <li key={index}><a href={resource.split(': ')[1]} target="_blank" rel="noopener noreferrer">{resource.split(': ')[1]}</a></li>
              ))}
            </ul>
          </Card.Body>
        </Card>
      )}

      {/* Career Recommendations & Road Map */}
      <Card className="mb-4">
        <Card.Header>Career Recommendations</Card.Header>
        <Card.Body>
          <ul>
            {recommendations.map((line, index) => (
              <li key={index}>{line}</li>
            ))}
          </ul>
          <Button variant="primary" onClick={downloadPDF}>Download Report</Button>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default Dashboard;
