import React, { useEffect, useState } from "react";
import { Container, Form, Button, Alert } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({ skills: "", educationLevel: "", degree: "", experience: "", jobRole: "" });
  const [response, setResponse] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      alert("Please log in first!");
      navigate("/");
    }
  }, [navigate]);

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResponse("");

    try {
      const res = await fetch("http://localhost:5000/api/career-recommendation", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const data = await res.json();
      setResponse(data.recommendations || "No recommendations available.");
    } catch {
      setResponse("Error fetching career recommendations.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container className="mt-5">
      <h2 className="text-center">Skill Gap Analysis & Career Recommendations</h2>
      <Form onSubmit={handleSubmit} className="mt-4">
        <Form.Group className="mb-3">
          <Form.Label>Skills</Form.Label>
          <Form.Control type="text" name="skills" placeholder="Enter your skills" value={formData.skills} onChange={handleChange} required />
        </Form.Group>
        <Button variant="primary" type="submit" className="w-100" disabled={loading}>
          {loading ? "Analyzing..." : "Get Career Recommendations"}
        </Button>
      </Form>
      {response && <Alert className="mt-4" variant="success"><h4>AI Recommendations</h4><p>{response}</p></Alert>}
    </Container>
  );
};

export default Dashboard;
