import React, { useState, useEffect } from "react";
import { Container, Button, Form, Spinner, Card, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Chart } from "react-google-charts";
import { jsPDF } from "jspdf";

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    skills: "",
    educationLevel: "",
    degree: "",
    experience: "",
    jobRole: ""
  });
  const [loading, setLoading] = useState(false);
  const [skillGap, setSkillGap] = useState("");
  const [roadmap, setRoadmap] = useState("");
  const [careerRecommendation, setCareerRecommendation] = useState("");
  const [learningResources, setLearningResources] = useState("");

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");

    if (!token || !userData) {
      alert("Please log in first!");
      navigate("/");
    }

    setUser(userData);
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post("http://localhost:5000/career/career-recommendation", formData, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });

      setSkillGap(response.data.skillGap);
      setRoadmap(response.data.roadmap);
      setCareerRecommendation(response.data.careerRecommendation);
      setLearningResources(response.data.learningResources);
    } catch (error) {
      alert("Error fetching recommendations.");
    } finally {
      setLoading(false);
    }
  };

  const downloadPDF = () => {
    const doc = new jsPDF("p", "mm", "a4");
    doc.setFont("helvetica", "bold");
    doc.setFontSize(22);
    doc.text("PathToGrow - Career Report", 14, 20);
    doc.setFontSize(18);
    doc.text("Skill Gap Analysis", 14, 30);
    doc.setFontSize(12);
    doc.text(skillGap, 14, 40);
    doc.text("Roadmap", 14, 70);
    doc.text(roadmap, 14, 80);
    doc.text("Career Recommendations", 14, 110);
    doc.text(careerRecommendation, 14, 120);
    doc.text("Learning Resources", 14, 150);
    doc.text(learningResources, 14, 160);
    doc.save("career_report.pdf");
  };

  const skillData = skillGap
    ? [["Skill", "Current Level", "Required Level"], ...skillGap.split("\n").map((skill) => skill.split(":"))]
    : [];

  return (
    <Container>
      <h2 className="text-center mt-4">Career Guidance Dashboard</h2>
      <Form onSubmit={handleSubmit} className="mb-4">
        <Row>
          <Col md={6}>
            <Form.Group className="mb-3">
              <Form.Label>Your Skills</Form.Label>
              <Form.Control type="text" name="skills" value={formData.skills} onChange={handleChange} required />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Education Level</Form.Label>
              <Form.Select name="educationLevel" value={formData.educationLevel} onChange={handleChange} required>
                <option value="">Select</option>
                <option value="High School">High School</option>
                <option value="Bachelor's">Bachelor's</option>
                <option value="Master's">Master's</option>
                <option value="PhD">PhD</option>
              </Form.Select>
            </Form.Group>
          </Col>
          <Col md={6}>
            <Form.Group className="mb-3">
              <Form.Label>Experience (Years)</Form.Label>
              <Form.Control type="text" name="experience" value={formData.experience} onChange={handleChange} required />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Desired Job Role</Form.Label>
              <Form.Control type="text" name="jobRole" value={formData.jobRole} onChange={handleChange} required />
            </Form.Group>
          </Col>
        </Row>
        <Button type="submit" disabled={loading}>{loading ? "Analyzing..." : "Get Recommendations"}</Button>
      </Form>

      {skillGap && <Chart chartType="Bar" data={skillData} width="100%" height="400px" />}
      <Card><Card.Body>{careerRecommendation}</Card.Body></Card>
      <Card><Card.Body>{roadmap}</Card.Body></Card>
      <Card><Card.Body>{learningResources}</Card.Body></Card>
      <Button onClick={downloadPDF}>Download PDF</Button>
    </Container>
  );
};

export default Dashboard;
