import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import axios from "axios";
import { useNavigate } from "react-router-dom";  // Import useNavigate

const AuthModal = ({ show, handleClose }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({ firstName: "", lastName: "", email: "", password: "" });
  const navigate = useNavigate();  // Use navigate to redirect after login

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const url = isLogin ? "http://localhost:5000/auth/login" : "http://localhost:5000/auth/register";

    try {
      const response = await axios.post(url, formData);
      alert(response.data.message);
      
      if (isLogin) {
        localStorage.setItem("token", response.data.token);
        navigate("/dashboard"); // ✅ Redirect to dashboard after login
      }
      
      setFormData({ firstName: "", lastName: "", email: "", password: "" }); // ✅ Clear fields after submission
      handleClose();
    } catch (error) {
      alert(error.response?.data?.message || "Something went wrong");
    }
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{isLogin ? "Login" : "Register"}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          {!isLogin && (
            <>
              <Form.Group className="mb-3">
                <Form.Label>First Name</Form.Label>
                <Form.Control type="text" name="firstName" onChange={handleChange} required />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Last Name</Form.Label>
                <Form.Control type="text" name="lastName" onChange={handleChange} required />
              </Form.Group>
            </>
          )}
          <Form.Group className="mb-3">
            <Form.Label>Email</Form.Label>
            <Form.Control type="email" name="email" onChange={handleChange} required />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" name="password" onChange={handleChange} required />
          </Form.Group>
          <Button type="submit" className="w-100">{isLogin ? "Login" : "Register"}</Button>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="link" onClick={() => setIsLogin(!isLogin)}>
          {isLogin ? "Need an account? Register" : "Already have an account? Login"}
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AuthModal;
