import React, { useState, useEffect } from "react";
import { Container, Button, Form, Spinner, Card, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { jsPDF } from "jspdf";
import { Chart } from "react-google-charts";  // Import Google Charts

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    skills: "",
    educationLevel: "",
    degree: "",
    experience: "",
    jobRole: ""
  });
  const [loading, setLoading] = useState(false);
  const [recommendations, setRecommendations] = useState("");
  const [skillGapData, setSkillGapData] = useState([["Skill", "Current Level", "Required Level"]]);
  const [learningResources, setLearningResources] = useState([]);

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");

    if (!token || !userData) {
      alert("Please log in first!");
      navigate("/");
    }

    setUser(userData);
    setFormData({
      firstName: userData.firstName,
      lastName: userData.lastName,
    });
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setRecommendations("");
    setSkillGapData([["Skill", "Current Level", "Required Level"]]);
    setLearningResources([]);

    try {
      const response = await axios.post(
        "http://localhost:5000/career/career-recommendation",
        formData,
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );

      const { recommendations, skill_gap, learning_resources } = response.data;
      setRecommendations(recommendations);

      const chartData = [["Skill", "Current Level", "Required Level"]];
      skill_gap.forEach(skill => {
        chartData.push([skill.name, skill.current_level, skill.required_level]);
      });

      setSkillGapData(chartData);
      setLearningResources(learning_resources);
      
    } catch (error) {
      setRecommendations("Error fetching career recommendations.");
    } finally {
      setLoading(false);
    }
  };

  const downloadPDF = () => {
    const doc = new jsPDF("p", "mm", "a4");

    doc.setFont("helvetica", "bold");
    doc.setFontSize(22);
    doc.text("PathToGrow", 14, 20);

    doc.setFontSize(18);
    doc.setFont("helvetica", "normal");
    doc.text("Career Recommendations", 14, 30);
    doc.line(14, 32, 200, 32);

    doc.setFontSize(12);
    const recommendationsArray = recommendations.split('\n');
    let yOffset = 40;

    recommendationsArray.forEach((paragraph) => {
      doc.text(paragraph, 14, yOffset);
      yOffset += 10;
      if (yOffset > 270) {
        doc.addPage();
        yOffset = 20;
      }
    });

    doc.save("career_recommendations.pdf");
  };

  return (
    <div className="dashboard-page py-5" style={{ backgroundColor: "#f8f9fa" }}>
      <Container>
        <Card className="shadow mb-5 border-0 rounded">
          <Card.Body className="p-4">
            <Row className="align-items-center">
              <Col md={8}>
                <h3 className="fw-bold mb-1">Welcome, {user?.firstName} {user?.lastName}!</h3>
              </Col>
              <Col md={4} className="text-end">
                <Button variant="outline-danger" className="rounded-pill" onClick={() => {
                  localStorage.removeItem("token");
                  localStorage.removeItem("user");
                  navigate("/");
                }}>
                  Logout
                </Button>
              </Col>
            </Row>
          </Card.Body>
        </Card>

        <Card className="shadow border-0 mb-5">
          <Card.Body className="p-4">
            <Form onSubmit={handleSubmit}>
              <Row>
                <Col md={6}>
                  <Form.Group controlId="skills">
                    <Form.Label>Your Skills</Form.Label>
                    <Form.Control type="text" name="skills" onChange={handleChange} />
                  </Form.Group>

                  <Form.Group controlId="educationLevel">
                    <Form.Label>Education Level</Form.Label>
                    <Form.Select name="educationLevel" onChange={handleChange}>
                      <option>Select your education level</option>
                      <option>High School</option>
                      <option>Bachelor's Degree</option>
                      <option>Master's Degree</option>
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group controlId="jobRole">
                    <Form.Label>Desired Job Role</Form.Label>
                    <Form.Control type="text" name="jobRole" onChange={handleChange} />
                  </Form.Group>
                  <Button type="submit" disabled={loading}>Analyze</Button>
                </Col>
              </Row>
            </Form>
          </Card.Body>
        </Card>

        {recommendations && !loading && (
          <>
            <Chart chartType="BarChart" data={skillGapData} width="100%" height="400px" />
            <h5>Learning Resources</h5>
            <ul>{learningResources.map((res, idx) => <li key={idx}><a href={res.link}>{res.title}</a></li>)}</ul>
            <Button onClick={downloadPDF}>Download PDF</Button>
          </>
        )}
      </Container>
    </div>
  );
};

export default Dashboard;
