import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import axios from "axios";

const AuthModal = ({ show, handleClose }) => {
  const [isLogin, setIsLogin] = useState(true); // Toggle between Login & Register
  const [formData, setFormData] = useState({ firstName: "", lastName: "", email: "", password: "" });

  const toggleForm = () => {
    setIsLogin(!isLogin);
    setFormData({ firstName: "", lastName: "", email: "", password: "" });
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = isLogin ? "http://localhost:5000/auth/login" : "http://localhost:5000/auth/register";
      const { data } = await axios.post(url, formData);

      alert(isLogin ? "Login Successful!" : "Registration Successful! Please log in.");
      localStorage.setItem("token", data.token);
      handleClose();
    } catch (error) {
      alert(error.response?.data?.message || "Something went wrong");
    }
  };

  return (
    <Modal show={show} onHide={handleClose} centered>
      <Modal.Header closeButton>
        <Modal.Title>{isLogin ? "Login" : "Register"}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          {!isLogin && (
            <>
              <Form.Group className="mb-3">
                <Form.Label>First Name</Form.Label>
                <Form.Control type="text" name="firstName" value={formData.firstName} onChange={handleChange} required />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Last Name</Form.Label>
                <Form.Control type="text" name="lastName" value={formData.lastName} onChange={handleChange} required />
              </Form.Group>
            </>
          )}
          <Form.Group className="mb-3">
            <Form.Label>Email</Form.Label>
            <Form.Control type="email" name="email" value={formData.email} onChange={handleChange} required />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" name="password" value={formData.password} onChange={handleChange} required />
          </Form.Group>
          <Button variant="primary" type="submit" className="w-100">
            {isLogin ? "Login" : "Register"}
          </Button>
        </Form>
        <div className="text-center mt-3">
          <p>
            {isLogin ? "Don't have an account?" : "Already have an account?"}{" "}
            <span style={{ cursor: "pointer", color: "blue" }} onClick={toggleForm}>
              {isLogin ? "Register here" : "Login here"}
            </span>
          </p>
        </div>
      </Modal.Body>
    </Modal>
  );
};

export default AuthModal;
