import React, { useState } from "react";
import { Container, Button, Navbar, Nav } from "react-bootstrap";
import AuthModal from "../components/AuthModal";

const Home = () => {
  const [showAuthModal, setShowAuthModal] = useState(false);

  return (
    <>
      {/* Navigation Bar */}
      <Navbar expand="lg" bg="white" className="py-3">
        <Container>
          <Navbar.Brand href="#">
            <img src="/images/LOGO.png" alt="PathToGrow Logo" />
            <span style={{ fontFamily: "'Fira Mono'" }}>PathToGrow</span>
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="navbarNav" />
          <Navbar.Collapse id="navbarNav">
            <Nav className="me-auto justify-content-center">
              <Nav.Link href="#">Home</Nav.Link>
              <Nav.Link href="#">About</Nav.Link>
              <Nav.Link href="#">Features</Nav.Link>
              <Nav.Link href="#">Contact</Nav.Link>
            </Nav>
            <Button variant="dark" className="rounded-pill px-4" onClick={() => setShowAuthModal(true)}>
              Sign Up
            </Button>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* Hero Section */}
      <Container className="text-center mt-5">
        <h1 className="fw-bold">Path To Grow</h1>
        <h2 className="text-muted">Bridge Your Skill Gap with AI</h2>
        <p>Personalized career guidance and learning roadmaps tailored just for you</p>
        <Button variant="primary" className="mt-3" onClick={() => setShowAuthModal(true)}>
          Get Started
        </Button>
      </Container>

      {/* Auth Modal */}
      <AuthModal show={showAuthModal} handleClose={() => setShowAuthModal(false)} />
    </>
  );
};

export default Home;
