import React, { useState, useEffect } from "react";
import { Container, Button, Form, Spinner, Card, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Chart } from "react-google-charts";
import { jsPDF } from "jspdf";

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    skills: "",
    educationLevel: "",
    degree: "",
    experience: "",
    jobRole: "",
  });
  const [loading, setLoading] = useState(false);
  const [recommendations, setRecommendations] = useState("");
  const [skillGapData, setSkillGapData] = useState([]);
  const [roadmap, setRoadmap] = useState("");
  const [learningResources, setLearningResources] = useState([]);

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("user"));
    if (!userData) {
      alert("Please log in first!");
      navigate("/");
    }
    setUser(userData);
    setFormData({ ...formData, firstName: userData.firstName, lastName: userData.lastName });
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setRecommendations("");
    setSkillGapData([]);
    setRoadmap("");
    setLearningResources([]);

    try {
      const response = await axios.post(
        "http://localhost:5000/career/career-recommendation",
        formData,
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );

      const aiResponse = response.data.recommendations;
      const parsedData = parseAIResponse(aiResponse);

      setRecommendations(parsedData.careerRecommendation);
      setSkillGapData(parsedData.skillGap);
      setRoadmap(parsedData.roadmap);
      setLearningResources(parsedData.learningResources);
    } catch (error) {
      setRecommendations("Error fetching recommendations.");
    } finally {
      setLoading(false);
    }
  };

  const parseAIResponse = (response) => {
    const sections = response.split("\n\n");
    return {
      careerRecommendation: sections[0] || "",
      skillGap: sections[1] ? [["Skill", "Proficiency Level"], ...sections[1].split("\n").map(skill => skill.split(": "))] : [],
      roadmap: sections[2] || "",
      learningResources: sections[3] ? sections[3].split("\n").map((item) => item.split(": ")) : [],
    };
  };

  const downloadPDF = () => {
    const doc = new jsPDF();
    doc.setFont("helvetica", "bold");
    doc.setFontSize(22);
    doc.text("PathToGrow", 14, 20);
    doc.setFontSize(18);
    doc.text("Career Report", 14, 30);
    doc.line(14, 32, 200, 32);
    doc.setFont("helvetica", "normal");
    doc.setFontSize(12);
    doc.text(recommendations, 14, 40);
    doc.save("career_report.pdf");
  };

  return (
    <Container>
      <h2 className="text-center">Career Analysis</h2>
      <Form onSubmit={handleSubmit}>
        <Row>
          <Col md={6}>
            <Form.Group>
              <Form.Label>Skills</Form.Label>
              <Form.Control type="text" name="skills" onChange={handleChange} required />
            </Form.Group>
            <Form.Group>
              <Form.Label>Education Level</Form.Label>
              <Form.Control as="select" name="educationLevel" onChange={handleChange} required>
                <option value="">Select</option>
                <option value="High School">High School</option>
                <option value="Bachelor's Degree">Bachelor's Degree</option>
                <option value="Master's Degree">Master's Degree</option>
              </Form.Control>
            </Form.Group>
          </Col>
          <Col md={6}>
            <Form.Group>
              <Form.Label>Job Role</Form.Label>
              <Form.Control type="text" name="jobRole" onChange={handleChange} required />
            </Form.Group>
            <Form.Group>
              <Form.Label>Experience</Form.Label>
              <Form.Control as="select" name="experience" onChange={handleChange} required>
                <option value="">Select</option>
                <option value="0-1">0-1 years</option>
                <option value="1-3">1-3 years</option>
                <option value="3-5">3-5 years</option>
              </Form.Control>
            </Form.Group>
          </Col>
        </Row>
        <Button type="submit" disabled={loading}>
          {loading ? <Spinner size="sm" /> : "Analyze"}
        </Button>
      </Form>

      <Card>
        <Card.Body>
          <h4>Skill Gap Analysis</h4>
          <Chart chartType="BarChart" width="100%" height="300px" data={skillGapData} />
        </Card.Body>
      </Card>

      <Card>
        <Card.Body>
          <h4>Roadmap</h4>
          <p>{roadmap}</p>
        </Card.Body>
      </Card>

      <Card>
        <Card.Body>
          <h4>Learning Resources</h4>
          <ul>
            {learningResources.map(([name, url], index) => (
              <li key={index}>
                <a href={url} target="_blank" rel="noopener noreferrer">{name}</a>
              </li>
            ))}
          </ul>
        </Card.Body>
      </Card>

      <Button onClick={downloadPDF}>Download Report</Button>
    </Container>
  );
};

export default Dashboard;
