import React, { useState, useEffect } from "react";
import { Container, Button, Form, Spinner, Card, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { jsPDF } from "jspdf";
import { Chart } from "react-google-charts";

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({ firstName: "", lastName: "" });
  const [loading, setLoading] = useState(false);
  const [recommendations, setRecommendations] = useState(null);

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");

    if (!token || !userData) {
      alert("Please log in first!");
      navigate("/");
    }

    setUser(userData);
    setFormData({ firstName: userData.firstName, lastName: userData.lastName });
  }, [navigate]);

  const handleProfileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.put(
        "http://localhost:5000/auth/edit-profile",
        formData,
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      localStorage.setItem("user", JSON.stringify(response.data.user));
      setUser(response.data.user);
      setIsEditing(false);
    } catch (error) {
      alert("Error updating profile.");
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setRecommendations(null);

    try {
      const response = await axios.post(
        "http://localhost:5000/career/career-recommendation",
        formData,
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      setRecommendations(response.data.recommendations);
    } catch (error) {
      setRecommendations("Error fetching career recommendations.");
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/");
  };

  const skillGapData = recommendations?.skillGap
    ? [
        ["Skill", "Current Proficiency", "Required Proficiency"],
        ...recommendations.skillGap.currentSkills.map((skill, index) => [
          skill.name,
          skill.proficiency,
          recommendations.skillGap.requiredSkills[index]?.proficiency || 100,
        ]),
      ]
    : [["Skill", "Current Proficiency", "Required Proficiency"], ["Loading", 0, 0]];

  return (
    <Container className="py-5">
      <Card className="shadow mb-5 border-0 rounded">
        <Card.Body>
          <Row className="align-items-center">
            <Col md={2} className="text-center">
              <div className="avatar-placeholder bg-primary rounded-circle d-flex align-items-center justify-content-center mx-auto" style={{ width: "80px", height: "80px" }}>
                <h2 className="text-white m-0">{user?.firstName?.[0]}{user?.lastName?.[0]}</h2>
              </div>
            </Col>
            <Col md={8}>
              <h3>Welcome, {user?.firstName} {user?.lastName}!</h3>
              <p>{user?.email}</p>
            </Col>
            <Col md={2} className="text-end">
              <Button variant="outline-danger" onClick={handleLogout}>Logout</Button>
            </Col>
          </Row>
        </Card.Body>
      </Card>

      {isEditing ? (
        <Card>
          <Card.Body>
            <Form onSubmit={handleProfileSubmit}>
              <Form.Group>
                <Form.Label>First Name</Form.Label>
                <Form.Control type="text" name="firstName" value={formData.firstName} onChange={handleProfileChange} />
              </Form.Group>
              <Form.Group>
                <Form.Label>Last Name</Form.Label>
                <Form.Control type="text" name="lastName" value={formData.lastName} onChange={handleProfileChange} />
              </Form.Group>
              <Button type="submit" disabled={loading}>{loading ? "Saving..." : "Save Changes"}</Button>
              <Button variant="secondary" onClick={() => setIsEditing(false)}>Cancel</Button>
            </Form>
          </Card.Body>
        </Card>
      ) : (
        <Button onClick={() => setIsEditing(true)}>Edit Profile</Button>
      )}

      <Form onSubmit={handleSubmit}>
        <Form.Group><Form.Control type="text" name="skills" onChange={handleProfileChange} placeholder="Skills" /></Form.Group>
        <Button type="submit" disabled={loading}>{loading ? "Loading..." : "Get Career Advice"}</Button>
      </Form>

      {recommendations && (
        <>
          <Card><Card.Body><h5>ðŸ“Š Skill Gap Analysis</h5><Chart chartType="BarChart" width="100%" height="300px" data={skillGapData} /></Card.Body></Card>
          <Card><Card.Body><h5>ðŸ“š Learning Resources</h5><p>{JSON.stringify(recommendations.learningResources)}</p></Card.Body></Card>
        </>
      )}
    </Container>
  );
};

export default Dashboard;
