import React, { useState, useEffect } from "react";
import { Container, Button, Form, Spinner, Card, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { jsPDF } from "jspdf"; // Import jsPDF

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    skills: "",
    educationLevel: "",
    degree: "",
    experience: "",
    jobRole: "",
  });
  const [loading, setLoading] = useState(false);
  const [recommendations, setRecommendations] = useState("");

  useEffect(() => {
    // Get user data from localStorage
    const userData = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");

    // If no token or user data, redirect to login page
    if (!token || !userData) {
      alert("Please log in first!");
      navigate("/"); // Redirect to login page
    }

    // Set the user data in state
    setUser(userData);
    setFormData({
      firstName: userData.firstName,
      lastName: userData.lastName,
    });
  }, [navigate]);

  const handleProfileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.put(
        "http://localhost:5000/auth/edit-profile", // Backend route to update profile
        formData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`, // Fixed template literal syntax
          },
        }
      );
      // Update user data in localStorage
      localStorage.setItem("user", JSON.stringify(response.data.user));
      // Set the updated user data in the state
      setUser(response.data.user);
      setIsEditing(false); // Close the profile edit form
    } catch (error) {
      alert("Error updating profile.");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setRecommendations(""); // Clear previous recommendations
    try {
      const response = await axios.post(
        "http://localhost:5000/career/career-recommendation",
        formData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`, // Fixed template literal syntax
          },
        }
      );
      setRecommendations(response.data.recommendations);
    } catch (error) {
      setRecommendations("Error fetching career recommendations.");
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    // Clear localStorage
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    // Redirect to home/login page
    navigate("/");
  };

  const downloadPDF = () => {
    const doc = new jsPDF("p", "mm", "a4"); // A4 size, portrait orientation

    // Add "PathToGrow" at the top of the page
    doc.setFont("helvetica", "bold");
    doc.setFontSize(22);
    doc.text("PathToGrow", 14, 20); // Positioning the company name at the top left

    // Add Title: "Career Recommendations"
    doc.setFontSize(18);
    doc.setFont("helvetica", "normal");
    doc.text("Career Recommendations", 14, 30);

    // Add a horizontal line after the title
    doc.setLineWidth(0.5);
    doc.line(14, 32, 200, 32);

    // Add recommendations content
    doc.setFont("helvetica", "normal");
    doc.setFontSize(12);
    const recommendationsArray = recommendations.split("\n"); // Fixed split syntax
    let yOffset = 40; // Set starting position for the content
    recommendationsArray.forEach((paragraph) => {
      doc.text(paragraph, 14, yOffset);
      yOffset += 10; // Increase yOffset for next line of text
      if (yOffset > 270) {
        // Check if we reached the bottom of the page
        doc.addPage(); // Add a new page if the content exceeds the current page
        yOffset = 20; // Reset position for the new page
      }
    });

    // Save PDF with a filename
    doc.save("career_recommendations.pdf");
  };

  return (
    <div className="dashboard-page py-5" style={{ backgroundColor: "#f8f9fa" }}>
      <Container>
        {/* User Profile Card */}
        <Card className="shadow mb-5 border-0 rounded">
          <Card.Body className="p-4">
            <Row className="align-items-center">
              <Col md={2} className="text-center">
                <div
                  className="avatar-placeholder bg-primary rounded-circle d-flex align-items-center justify-content-center mx-auto"
                  style={{ width: "80px", height: "80px" }}
                >
                  <h2 className="text-white m-0">
                    {user?.firstName?.[0]}{user?.lastName?.[0]}
                  </h2>
                </div>
              </Col>
              <Col md={8}>
                <h3 className="fw-bold mb-1">Welcome, {user?.firstName} {user?.lastName}!</h3>
                <p className="text-muted mb-0">
                  <i className="fas fa-envelope me-2"></i>{user?.email}
                </p>
              </Col>
              <Col md={2} className="text-end">
                <Button
                  variant="outline-danger"
                  className="rounded-pill"
                  onClick={handleLogout} // Logout Button
                >
                  <i className="fas fa-sign-out-alt me-2"></i>Logout
                </Button>
              </Col>
            </Row>
          </Card.Body>
        </Card>

        {/* Your Profile Edit Form */}
        {isEditing ? (
          <Card className="shadow mb-5 border-0">
            <Card.Body className="p-4">
              <Form onSubmit={handleProfileSubmit}>
                <h4 className="fw-bold mb-4 border-bottom pb-3">Edit Your Profile</h4>
                <Row>
                  <Col md={6}>
                    <Form.Group controlId="firstName" className="mb-3">
                      <Form.Label>First Name</Form.Label>
                      <Form.Control
                        type="text"
                        name="firstName"
                        value={formData.firstName}
                        onChange={handleProfileChange}
                        className="py-2"
                        required
                      />
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group controlId="lastName" className="mb-3">
                      <Form.Label>Last Name</Form.Label>
                      <Form.Control
                        type="text"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleProfileChange}
                        className="py-2"
                        required
                      />
                    </Form.Group>
                  </Col>
                </Row>
                <div className="d-flex justify-content-end mt-3">
                  <Button
                    variant="secondary"
                    className="me-2"
                    onClick={() => setIsEditing(false)}
                  >
                    Cancel
                  </Button>
                  <Button
                    variant="primary"
                    type="submit"
                    disabled={loading}
                  >
                    {loading ? <Spinner animation="border" size="sm" /> : "Save Changes"}
                  </Button>
                </div>
              </Form>
            </Card.Body>
          </Card>
        ) : (
          <>
            <div className="text-center mb-4">
              <h2 className="fw-bold">Your Skill Gap Analysis & Career Recommendations</h2>
              <p className="text-muted">Fill in the details below to receive personalized career guidance</p>
            </div>
            <Card className="shadow border-0 mb-5">
              <Card.Body className="p-4">
                <Form onSubmit={handleSubmit}>
                  <Row>
                    <Col md={6}>
                      <Form.Group controlId="skills" className="mb-3">
                        <Form.Label className="fw-bold">Your Skills</Form.Label>
                        <Form.Control
                          type="text"
                          name="skills"
                          value={formData.skills || ""}
                          onChange={handleChange}
                          placeholder="JavaScript, Project Management, etc."
                          className="py-2"
                        />
                      </Form.Group>
                      <Form.Group controlId="educationLevel" className="mb-3">
                        <Form.Label className="fw-bold">Education Level</Form.Label>
                        <Form.Select
                          name="educationLevel"
                          value={formData.educationLevel || ""}
                          onChange={handleChange}
                          className="py-2"
                        >
                          <option value="">Select your education level</option>
                          <option value="High School">High School</option>
                          <option value="Associate's Degree">Associate's Degree</option>
                          <option value="Bachelor's Degree">Bachelor's Degree</option>
                          <option value="Master's Degree">Master's Degree</option>
                          <option value="PhD">PhD</option>
                        </Form.Select>
                      </Form.Group>
                      <Form.Group controlId="degree" className="mb-3">
                        <Form.Label className="fw-bold">Degree/Field of Study</Form.Label>
                        <Form.Control
                          type="text"
                          name="degree"
                          value={formData.degree || ""}
                          onChange={handleChange}
                          placeholder="Computer Science, Business, etc."
                          className="py-2"
                        />
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="experience" className="mb-3">
                        <Form.Label className="fw-bold">Years of Experience</Form.Label>
                        <Form.Select
                          name="experience"
                          value={formData.experience || ""}
                          onChange={handleChange}
                          className="py-2"
                        >
                          <option value="">Select years of experience</option>
                          <option value="0-1">0-1 years</option>
                          <option value="1-3">1-3 years</option>
                          <option value="3-5">3-5 years</option>
                          <option value="5-10">5-10 years</option>
                          <option value="10+">10+ years</option>
                        </Form.Select>
                      </Form.Group>
                      <Form.Group controlId="jobRole" className="mb-3">
                        <Form.Label className="fw-bold">Desired Job Role</Form.Label>
                        <Form.Control
                          type="text"
                          name="jobRole"
                          value={formData.jobRole || ""}
                          onChange={handleChange}
                          placeholder="Data Scientist, UX Designer, etc."
                          className="py-2"
                        />
                      </Form.Group>
                      <div className="d-grid mt-4">
                        <Button
                          variant="primary"
                          type="submit"
                          size="lg"
                          className="py-2"
                          disabled={loading}
                        >
                          {loading ? (
                            <Spinner animation="border" size="sm" className="me-2" />
                          ) : (
                            <i className="fas fa-search me-2"></i>
                          )}
                          Analyze My Skills
                        </Button>
                      </div>
                    </Col>
                  </Row>
                </Form>
              </Card.Body>
            </Card>
            {/* Display Career Recommendations */}
            {recommendations && !loading && (
              <Card className="shadow border-0 animation-fade-in">
                <Card.Header className="bg-primary text-white py-3">
                  <h4 className="mb-0 fw-bold">
                    <i className="fas fa-chart-line me-2"></i>
                    Career Recommendations
                  </h4>
                </Card.Header>
                <Card.Body className="p-4">
                  <div className="recommendation-content">
                    {recommendations.split("\n").map((paragraph, index) => (
                      <p key={index}>{paragraph}</p>
                    ))}
                  </div>
                  <div className="text-center mt-4">
                    <Button variant="outline-primary" onClick={downloadPDF}>
                      <i className="fas fa-download me-2"></i>
                      Download Report
                    </Button>
                  </div>
                </Card.Body>
              </Card>
            )}
          </>
        )}
      </Container>
    </div>
  );
};

export default Dashboard;
