import React, { useState, useEffect } from "react";
import { Container, Button, Form, Spinner, Card, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { jsPDF } from "jspdf";
import { BarChart } from '@mui/x-charts/BarChart'; // Using MUI X Charts instead of Google Charts for simplicity

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    skills: "",
    educationLevel: "",
    degree: "",
    experience: "",
    jobRole: "",
  });
  const [loading, setLoading] = useState(false);
  const [careerRecommendation, setCareerRecommendation] = useState("");
  const [skillGap, setSkillGap] = useState("");
  const [roadmap, setRoadmap] = useState("");
  const [learningResources, setLearningResources] = useState("");
  const [skillGapData, setSkillGapData] = useState({ current: [], required: [], skills: [] });

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");

    if (!token || !userData) {
      alert("Please log in first!");
      navigate("/");
    }

    setUser(userData);
    setFormData((prev) => ({
      ...prev,
      firstName: userData.firstName,
      lastName: userData.lastName,
    }));
  }, [navigate]);

  const handleProfileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.put(
        "http://localhost:5000/auth/edit-profile",
        { firstName: formData.firstName, lastName: formData.lastName },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      localStorage.setItem("user", JSON.stringify(response.data.user));
      setUser(response.data.user);
      setIsEditing(false);
    } catch (error) {
      alert("Error updating profile.");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setCareerRecommendation("");
    setSkillGap("");
    setRoadmap("");
    setLearningResources("");
    setSkillGapData({ current: [], required: [], skills: [] });

    try {
      const response = await axios.post(
        "http://localhost:5000/career/career-recommendation",
        formData,
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      const text = response.data.recommendations;

      // Parse the response
      const sections = text.split("###").map(section => section.trim()).filter(Boolean);
      sections.forEach(section => {
        if (section.startsWith("Career Recommendation")) {
          setCareerRecommendation(section.replace("Career Recommendation", "").trim());
        } else if (section.startsWith("Skill Gap")) {
          setSkillGap(section.replace("Skill Gap", "").trim());
          parseSkillGap(section);
        } else if (section.startsWith("Roadmap")) {
          setRoadmap(section.replace("Roadmap", "").trim());
        } else if (section.startsWith("Learning Resources")) {
          setLearningResources(section.replace("Learning Resources", "").trim());
        }
      });
    } catch (error) {
      setCareerRecommendation("Error fetching career recommendations.");
    } finally {
      setLoading(false);
    }
  };

  const parseSkillGap = (text) => {
    const lines = text.split('\n').filter(line => line.includes('%'));
    const skills = [];
    const current = [];
    const required = [];

    lines.forEach(line => {
      const match = line.match(/(.+?)\s*-\s*Current:\s*(\d+)%\s*Required:\s*(\d+)%/i);
      if (match) {
        skills.push(match[1].trim());
        current.push(parseInt(match[2]));
        required.push(parseInt(match[3]));
      }
    });

    setSkillGapData({ skills, current, required });
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/");
  };

  const downloadPDF = () => {
    const doc = new jsPDF("p", "mm", "a4");
    doc.setFont("helvetica", "bold");
    doc.setFontSize(22);
    doc.text("PathToGrow", 14, 20);

    let yOffset = 30;
    const addSection = (title, content) => {
      doc.setFontSize(18);
      doc.text(title, 14, yOffset);
      yOffset += 2;
      doc.setLineWidth(0.5);
      doc.line(14, yOffset, 200, yOffset);
      yOffset += 8;
      doc.setFontSize(12);
      doc.setFont("helvetica", "normal");
      const splitText = doc.splitTextToSize(content, 180);
      splitText.forEach(line => {
        if (yOffset > 270) {
          doc.addPage();
          yOffset = 20;
        }
        doc.text(line, 14, yOffset);
        yOffset += 7;
      });
      yOffset += 10;
    };

    addSection("Career Recommendation", careerRecommendation);
    addSection("Skill Gap", skillGap);
    addSection("Roadmap", roadmap);
    addSection("Learning Resources", learningResources);

    doc.save("career_recommendations.pdf");
  };

  return (
    <div className="dashboard-page py-5" style={{ backgroundColor: "#f8f9fa" }}>
      <Container>
        <Card className="shadow mb-5 border-0 rounded">
          <Card.Body className="p-4">
            <Row className="align-items-center">
              <Col md={2} className="text-center">
                <div
                  className="avatar-placeholder bg-primary rounded-circle d-flex align-items-center justify-content-center mx-auto"
                  style={{ width: "80px", height: "80px" }}
                >
                  <h2 className="text-white m-0">
                    {user?.firstName?.[0]}{user?.lastName?.[0]}
                  </h2>
                </div>
              </Col>
              <Col md={7}>
                <h3 className="fw-bold mb-1">
                  Welcome, {user?.firstName} {user?.lastName}!
                </h3>
                <p className="text-muted mb-0">
                  <i className="fas fa-envelope me-2"></i>{user?.email}
                </p>
              </Col>
              <Col md={3} className="text-end">
                <Button
                  variant="outline-primary"
                  className="rounded-pill me-2"
                  onClick={() => setIsEditing(true)}
                >
                  <i className="fas fa-edit me-2"></i>Edit
                </Button>
                <Button
                  variant="outline-danger"
                  className="rounded-pill"
                  onClick={handleLogout}
                >
                  <i className="fas fa-sign-out-alt me-2"></i>Logout
                </Button>
              </Col>
            </Row>
          </Card.Body>
        </Card>

        {isEditing && (
          <Card className="shadow mb-5 border-0">
            <Card.Body className="p-4">
              <Form onSubmit={handleProfileSubmit}>
                <h4 className="fw-bold mb-4 border-bottom pb-3">Edit Your Profile</h4>
                <Row>
                  <Col md={6}>
                    <Form.Group controlId="firstName" className="mb-3">
                      <Form.Label>First Name</Form.Label>
                      <Form.Control
                        type="text"
                        name="firstName"
                        value={formData.firstName}
                        onChange={handleProfileChange}
                        className="py-2"
                        required
                      />
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group controlId="lastName" className="mb-3">
                      <Form.Label>Last Name</Form.Label>
                      <Form.Control
                        type="text"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleProfileChange}
                        className="py-2"
                        required
                      />
                    </Form.Group>
                  </Col>
                </Row>
                <div className="d-flex justify-content-end mt-3">
                  <Button
                    variant="secondary"
                    className="me-2"
                    onClick={() => setIsEditing(false)}
                  >
                    Cancel
                  </Button>
                  <Button
                    variant="primary"
                    type="submit"
                    disabled={loading}
                  >
                    {loading ? <Spinner animation="border" size="sm" /> : "Save Changes"}
                  </Button>
                </div>
              </Form>
            </Card.Body>
          </Card>
        )}

        {!isEditing && (
          <>
            <div className="text-center mb-4">
              <h2 className="fw-bold">Your Skill Gap Analysis & Career Recommendations</h2>
              <p className="text-muted">Fill in the details below to receive personalized career guidance</p>
            </div>

            <Card className="shadow border-0 mb-5">
              <Card.Body className="p-4">
                <Form onSubmit={handleSubmit}>
                  <Row>
                    <Col md={6}>
                      <Form.Group controlId="skills" className="mb-3">
                        <Form.Label className="fw-bold">Your Skills</Form.Label>
                        <Form.Control
                          type="text"
                          name="skills"
                          value={formData.skills}
                          onChange={handleChange}
                          placeholder="JavaScript, Project Management, etc."
                          className="py-2"
                        />
                      </Form.Group>

                      <Form.Group controlId="educationLevel" className="mb-3">
                        <Form.Label className="fw-bold">Education Level</Form.Label>
                        <Form.Select
                          name="educationLevel"
                          value={formData.educationLevel}
                          onChange={handleChange}
                          className="py-2"
                        >
                          <option value="">Select your education level</option>
                          <option value="High School">High School</option>
                          <option value="Associate's Degree">Associate's Degree</option>
                          <option value="Bachelor's Degree">Bachelor's Degree</option>
                          <option value="Master's Degree">Master's Degree</option>
                          <option value="PhD">PhD</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group controlId="degree" className="mb-3">
                        <Form.Label className="fw-bold">Degree/Field of Study</Form.Label>
                        <Form.Control
                          type="text"
                          name="degree"
                          value={formData.degree}
                          onChange={handleChange}
                          placeholder="Computer Science, Business, etc."
                          className="py-2"
                        />
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="experience" className="mb-3">
                        <Form.Label className="fw-bold">Years of Experience</Form.Label>
                        <Form.Select
                          name="experience"
                          value={formData.experience}
                          onChange={handleChange}
                          className="py-2"
                        >
                          <option value="">Select years of experience</option>
                          <option value="0-1">0-1 years</option>
                          <option value="1-3">1-3 years</option>
                          <option value="3-5">3-5 years</option>
                          <option value="5-10">5-10 years</option>
                          <option value="10+">10+ years</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group controlId="jobRole" className="mb-3">
                        <Form.Label className="fw-bold">Desired Job Role</Form.Label>
                        <Form.Control
                          type="text"
                          name="jobRole"
                          value={formData.jobRole}
                          onChange={handleChange}
                          placeholder="Data Scientist, UX Designer, etc."
                          className="py-2"
                        />
                      </Form.Group>

                      <div className="d-grid mt-4">
                        <Button
                          variant="primary"
                          type="submit"
                          size="lg"
                          className="py-2"
                          disabled={loading}
                        >
                          {loading ? (
                            <Spinner animation="border" size="sm" className="me-2" />
                          ) : (
                            <i className="fas fa-search me-2"></i>
                          )}
                          Analyze My Skills
                        </Button>
                      </div>
                    </Col>
                  </Row>
                </Form>
              </Card.Body>
            </Card>

            {(careerRecommendation || skillGap || roadmap || learningResources) && !loading && (
              <Row>
                <Col md={6} className="mb-4">
                  <Card className="shadow border-0">
                    <Card.Header className="bg-primary text-white py-3">
                      <h4 className="mb-0 fw-bold">Career Recommendation</h4>
                    </Card.Header>
                    <Card.Body className="p-4">
                      {careerRecommendation.split('\n').map((line, index) => (
                        <p key={index}>{line}</p>
                      ))}
                    </Card.Body>
                  </Card>
                </Col>

                <Col md={6} className="mb-4">
                  <Card className="shadow border-0">
                    <Card.Header className="bg-primary text-white py-3">
                      <h4 className="mb-0 fw-bold">Skill Gap</h4>
                    </Card.Header>
                    <Card.Body className="p-4">
                      {skillGap.split('\n').map((line, index) => (
                        <p key={index}>{line}</p>
                      ))}
                      {skillGapData.skills.length > 0 && (
                        <BarChart
                          xAxis={[{ scaleType: 'band', data: skillGapData.skills }]}
                          series={[
                            { data: skillGapData.current, label: 'Current Skill Level (%)' },
                            { data: skillGapData.required, label: 'Required Skill Level (%)' },
                          ]}
                          width={500}
                          height={300}
                        />
                      )}
                    </Card.Body>
                  </Card>
                </Col>

                <Col md={6} className="mb-4">
                  <Card className="shadow border-0">
                    <Card.Header className="bg-primary text-white py-3">
                      <h4 className="mb-0 fw-bold">Roadmap</h4>
                    </Card.Header>
                    <Card.Body className="p-4">
                      {roadmap.split('\n').map((line, index) => (
                        <p key={index}>{line}</p>
                      ))}
                    </Card.Body>
                  </Card>
                </Col>

                <Col md={6} className="mb-4">
                  <Card className="shadow border-0">
                    <Card.Header className="bg-primary text-white py-3">
                      <h4 className="mb-0 fw-bold">Learning Resources</h4>
                    </Card.Header>
                    <Card.Body className="p-4">
                      {learningResources.split('\n').map((line, index) => (
                        <p key={index}>{line}</p>
                      ))}
                      <div className="text-center mt-4">
                        <Button variant="outline-primary" onClick={downloadPDF}>
                          <i className="fas fa-download me-2"></i>Download Report
                        </Button>
                      </div>
                    </Card.Body>
                  </Card>
                </Col>
              </Row>
            )}
          </>
        )}
      </Container>
    </div>
  );
};

export default Dashboard;
