import React, { useState } from "react";
import axios from "axios"; // To handle HTTP requests
import "bootstrap/dist/css/bootstrap.min.css";

const Modal = ({ show, onClose }) => {
  const [isLogin, setIsLogin] = useState(true); // Toggle between login and register
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    firstName: "",
    lastName: "",
  }); // State to manage form data
  const [errorMessage, setErrorMessage] = useState(""); // State for error message

  // Handle form data changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage(""); // Reset the error message before submitting

    try {
      if (isLogin) {
        // Handle Login logic
        const response = await axios.post("http://localhost:5000/auth/login", formData); // Login API call
        console.log("Login response:", response.data); // Debug response
        alert("Login successful!");
        localStorage.setItem("token", response.data.token); // Store token in localStorage
        onClose(); // Close the modal on success
      } else {
        // Handle Registration logic
        const response = await axios.post("http://localhost:5000/auth/register", formData); // Register API call
        console.log("Registration response:", response.data); // Debug response
        alert("Registration successful! Please login.");
        setFormData({ email: "", password: "", firstName: "", lastName: "" }); // Clear form data after successful registration
        onClose(); // Close modal
      }
    } catch (error) {
      console.error("Error response:", error.response); // Log error
      setErrorMessage(error.response?.data.message || "An error occurred, please try again.");
    }
  };

  return (
    <div className={`modal fade ${show ? "show d-block" : "d-none"}`} tabIndex="-1" role="dialog">
      <div className="modal-dialog modal-dialog-centered">
        <div className="modal-content">
          {/* Modal Header */}
          <div className="modal-header">
            <h5 className="modal-title">{isLogin ? "Login" : "Register"}</h5>
            <button type="button" className="btn-close" onClick={onClose}></button>
          </div>

          {/* Modal Body */}
          <div className="modal-body">
            {/* Display error message */}
            {errorMessage && <div className="alert alert-danger">{errorMessage}</div>} {/* Show error if any */}

            <form onSubmit={handleSubmit}>
              {/* Show First & Last Name only for Register */}
              {!isLogin && (
                <>
                  <div className="mb-3">
                    <label className="form-label">First Name</label>
                    <input type="text" className="form-control" name="firstName" onChange={handleChange} required />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Last Name</label>
                    <input type="text" className="form-control" name="lastName" onChange={handleChange} required />
                  </div>
                </>
              )}

              <div className="mb-3">
                <label className="form-label">Email</label>
                <input type="email" className="form-control" name="email" onChange={handleChange} required />
              </div>
              <div className="mb-3">
                <label className="form-label">Password</label>
                <input type="password" className="form-control" name="password" onChange={handleChange} required />
              </div>
              <button type="submit" className="btn btn-primary w-100">
                {isLogin ? "Login" : "Register"}
              </button>
            </form>
          </div>

          {/* Modal Footer */}
          <div className="modal-footer">
            <p className="text-center w-100">
              {isLogin ? "Don't have an account?" : "Already have an account?"}{" "}
              <button className="btn btn-link" onClick={() => setIsLogin(!isLogin)}>
                {isLogin ? "Register here" : "Login here"}
              </button>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Modal;
