import React, { useState, useEffect } from "react";
import { Container, Button, Form, Spinner, Card, Row, Col, Alert } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { jsPDF } from "jspdf";
import { Chart } from "react-google-charts";

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    skills: "",
    educationLevel: "",
    degree: "",
    experience: "",
    jobRole: "",
    interests: "",
    certifications: "",
    location: "",
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [careerRecommendation, setCareerRecommendation] = useState("");
  const [skillGap, setSkillGap] = useState({ currentSkills: [], requiredSkills: [] });
  const [roadmap, setRoadmap] = useState([]);
  const [learningResources, setLearningResources] = useState({});

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");

    if (!token || !userData) {
      alert("Please log in first!");
      navigate("/");
    }

    setUser(userData);
    setFormData((prev) => ({
      ...prev,
      firstName: userData.firstName,
      lastName: userData.lastName,
    }));
  }, [navigate]);

  const handleProfileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    try {
      const response = await axios.put(
        "http://localhost:5000/auth/edit-profile",
        { firstName: formData.firstName, lastName: formData.lastName },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );
      localStorage.setItem("user", JSON.stringify(response.data.user));
      setUser(response.data.user);
      setIsEditing(false);
    } catch (error) {
      setError("Error updating profile. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setCareerRecommendation("");
    setSkillGap({ currentSkills: [], requiredSkills: [] });
    setRoadmap([]);
    setLearningResources({});

    try {
      const response = await axios.post(
        "http://localhost:5000/career/career-recommendation",
        formData,
        {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        }
      );

      // Handle potential error response from backend
      if (response.data.message) {
        setError(`${response.data.message}. Raw response: ${response.data.rawResponse || "N/A"}`);
        return;
      }

      const { careerRecommendation, skillGap, roadmap, learningResources } = response.data;
      setCareerRecommendation(careerRecommendation || "No recommendation provided.");
      setSkillGap(skillGap || { currentSkills: [], requiredSkills: [] });
      setRoadmap(roadmap || []);
      setLearningResources(learningResources || {});
    } catch (error) {
      setError(error.response?.data?.message || "Error fetching career recommendations.");
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setFormData({
      firstName: user.firstName,
      lastName: user.lastName,
      skills: "",
      educationLevel: "",
      degree: "",
      experience: "",
      jobRole: "",
      interests: "",
      certifications: "",
      location: "",
    });
    setCareerRecommendation("");
    setSkillGap({ currentSkills: [], requiredSkills: [] });
    setRoadmap([]);
    setLearningResources({});
    setError("");
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/");
  };

  const downloadPDF = () => {
    const doc = new jsPDF("p", "mm", "a4");
    doc.setFont("helvetica", "bold");
    doc.setFontSize(22);
    doc.text("PathToGrow", 14, 20);

    let yOffset = 30;
    const addSection = (title, content) => {
      doc.setFontSize(18);
      doc.text(title, 14, yOffset);
      yOffset += 2;
      doc.setLineWidth(0.5);
      doc.line(14, yOffset, 200, yOffset);
      yOffset += 8;
      doc.setFontSize(12);
      doc.setFont("helvetica", "normal");
      const splitText = doc.splitTextToSize(content, 180);
      splitText.forEach(line => {
        if (yOffset > 270) {
          doc.addPage();
          yOffset = 20;
        }
        doc.text(line, 14, yOffset);
        yOffset += 7;
      });
      yOffset += 10;
    };

    addSection("Career Recommendation", careerRecommendation);
    addSection(
      "Skill Gap",
      `Current Skills: ${skillGap.currentSkills.map(s => `${s.name}: ${s.proficiency}%`).join(", ")}\nRequired Skills: ${skillGap.requiredSkills.map(s => `${s.name}: ${s.proficiency}%`).join(", ")}`
    );
    addSection(
      "Roadmap",
      roadmap.map((step, i) => `${step.step}. ${step.description} (${step.duration})`).join("\n")
    );
    addSection(
      "Learning Resources",
      Object.entries(learningResources).map(([skill, url]) => `${skill}: ${url}`).join("\n")
    );

    doc.save("career_recommendations.pdf");
  };

  const chartData = [
    ["Skill", "Current (%)", "Required (%)"],
    ...skillGap.currentSkills.map((current, i) => [
      current.name,
      current.proficiency,
      skillGap.requiredSkills.find(s => s.name === current.name)?.proficiency || 0,
    ]),
    ...skillGap.requiredSkills
      .filter(req => !skillGap.currentSkills.some(cur => cur.name === req.name))
      .map(req => [req.name, 0, req.proficiency]),
  ];

  return (
    <div className="dashboard-page py-5" style={{ backgroundColor: "#f8f9fa" }}>
      <Container>
        <Card className="shadow mb-5 border-0 rounded">
          <Card.Body className="p-4">
            <Row className="align-items-center">
              <Col md={2} className="text-center">
                <div
                  className="avatar-placeholder bg-primary rounded-circle d-flex align-items-center justify-content-center mx-auto"
                  style={{ width: "80px", height: "80px" }}
                >
                  <h2 className="text-white m-0">
                    {user?.firstName?.[0]}{user?.lastName?.[0]}
                  </h2>
                </div>
              </Col>
              <Col md={7}>
                <h3 className="fw-bold mb-1">
                  Welcome, {user?.firstName} {user?.lastName}!
                </h3>
                <p className="text-muted mb-0">
                  <i className="fas fa-envelope me-2"></i>{user?.email}
                </p>
              </Col>
              <Col md={3} className="text-end">
                <Button
                  variant="outline-primary"
                  className="rounded-pill me-2"
                  onClick={() => setIsEditing(true)}
                >
                  <i className="fas fa-edit me-2"></i>Edit
                </Button>
                <Button
                  variant="outline-danger"
                  className="rounded-pill"
                  onClick={handleLogout}
                >
                  <i className="fas fa-sign-out-alt me-2"></i>Logout
                </Button>
              </Col>
            </Row>
          </Card.Body>
        </Card>

        {isEditing && (
          <Card className="shadow mb-5 border-0">
            <Card.Body className="p-4">
              <Form onSubmit={handleProfileSubmit}>
                <h4 className="fw-bold mb-4 border-bottom pb-3">Edit Your Profile</h4>
                {error && <Alert variant="danger">{error}</Alert>}
                <Row>
                  <Col md={6}>
                    <Form.Group controlId="firstName" className="mb-3">
                      <Form.Label>First Name</Form.Label>
                      <Form.Control
                        type="text"
                        name="firstName"
                        value={formData.firstName}
                        onChange={handleProfileChange}
                        className="py-2"
                        required
                      />
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group controlId="lastName" className="mb-3">
                      <Form.Label>Last Name</Form.Label>
                      <Form.Control
                        type="text"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleProfileChange}
                        className="py-2"
                        required
                      />
                    </Form.Group>
                  </Col>
                </Row>
                <div className="d-flex justify-content-end mt-3">
                  <Button
                    variant="secondary"
                    className="me-2"
                    onClick={() => setIsEditing(false)}
                  >
                    Cancel
                  </Button>
                  <Button
                    variant="primary"
                    type="submit"
                    disabled={loading}
                  >
                    {loading ? <Spinner animation="border" size="sm" /> : "Save Changes"}
                  </Button>
                </div>
              </Form>
            </Card.Body>
          </Card>
        )}

        {!isEditing && (
          <>
            <div className="text-center mb-4">
              <h2 className="fw-bold">Your Skill Gap Analysis & Career Recommendations</h2>
              <p className="text-muted">Fill in the details below to receive personalized career guidance</p>
            </div>

            <Card className="shadow border-0 mb-5">
              <Card.Body className="p-4">
                <Form onSubmit={handleSubmit}>
                  {error && <Alert variant="danger">{error}</Alert>}
                  <Row>
                    <Col md={6}>
                      <Form.Group controlId="skills" className="mb-3">
                        <Form.Label className="fw-bold">Your Skills</Form.Label>
                        <Form.Control
                          type="text"
                          name="skills"
                          value={formData.skills}
                          onChange={handleChange}
                          placeholder="JavaScript, Python, etc."
                          className="py-2"
                          required
                        />
                      </Form.Group>

                      <Form.Group controlId="educationLevel" className="mb-3">
                        <Form.Label className="fw-bold">Education Level</Form.Label>
                        <Form.Select
                          name="educationLevel"
                          value={formData.educationLevel}
                          onChange={handleChange}
                          className="py-2"
                          required
                        >
                          <option value="">Select your education level</option>
                          <option value="High School">High School</option>
                          <option value="Associate's Degree">Associate's Degree</option>
                          <option value="Bachelor's Degree">Bachelor's Degree</option>
                          <option value="Master's Degree">Master's Degree</option>
                          <option value="PhD">PhD</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group controlId="degree" className="mb-3">
                        <Form.Label className="fw-bold">Degree/Field of Study</Form.Label>
                        <Form.Control
                          type="text"
                          name="degree"
                          value={formData.degree}
                          onChange={handleChange}
                          placeholder="Computer Science, Business, etc."
                          className="py-2"
                        />
                      </Form.Group>

                      <Form.Group controlId="experience" className="mb-3">
                        <Form.Label className="fw-bold">Years of Experience</Form.Label>
                        <Form.Select
                          name="experience"
                          value={formData.experience}
                          onChange={handleChange}
                          className="py-2"
                        >
                          <option value="">Select years of experience</option>
                          <option value="0-1">0-1 years</option>
                          <option value="1-3">1-3 years</option>
                          <option value="3-5">3-5 years</option>
                          <option value="5-10">5-10 years</option>
                          <option value="10+">10+ years</option>
                        </Form.Select>
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="jobRole" className="mb-3">
                        <Form.Label className="fw-bold">Desired Job Role</Form.Label>
                        <Form.Control
                          type="text"
                          name="jobRole"
                          value={formData.jobRole}
                          onChange={handleChange}
                          placeholder="Data Scientist, UX Designer, etc."
                          className="py-2"
                          required
                        />
                      </Form.Group>

                      <Form.Group controlId="interests" className="mb-3">
                        <Form.Label className="fw-bold">Interests</Form.Label>
                        <Form.Control
                          type="text"
                          name="interests"
                          value={formData.interests}
                          onChange={handleChange}
                          placeholder="AI, Web Development, etc."
                          className="py-2"
                        />
                      </Form.Group>

                      <Form.Group controlId="certifications" className="mb-3">
                        <Form.Label className="fw-bold">Certifications</Form.Label>
                        <Form.Control
                          type="text"
                          name="certifications"
                          value={formData.certifications}
                          onChange={handleChange}
                          placeholder="AWS Certified, PMP, etc."
                          className="py-2"
                        />
                      </Form.Group>

                      <Form.Group controlId="location" className="mb-3">
                        <Form.Label className="fw-bold">Location Preference</Form.Label>
                        <Form.Control
                          type="text"
                          name="location"
                          value={formData.location}
                          onChange={handleChange}
                          placeholder="New York, Remote, etc."
                          className="py-2"
                        />
                      </Form.Group>

                      <div className="d-flex justify-content-between mt-4">
                        <Button
                          variant="outline-secondary"
                          onClick={handleReset}
                          disabled={loading}
                        >
                          <i className="fas fa-undo me-2"></i>Reset
                        </Button>
                        <Button
                          variant="primary"
                          type="submit"
                          disabled={loading}
                        >
                          {loading ? (
                            <Spinner animation="border" size="sm" className="me-2" />
                          ) : (
                            <i className="fas fa-search me-2"></i>
                          )}
                          Analyze My Skills
                        </Button>
                      </div>
                    </Col>
                  </Row>
                </Form>
              </Card.Body>
            </Card>

            {(careerRecommendation || skillGap.currentSkills.length > 0 || roadmap.length > 0 || Object.keys(learningResources).length > 0) && !loading && (
              <Row>
                <Col md={6} lg={3} className="mb-4">
                  <Card className="shadow border-0 h-100">
                    <Card.Header className="bg-success text-white py-3">
                      <h4 className="mb-0 fw-bold">Career Recommendation</h4>
                    </Card.Header>
                    <Card.Body className="p-4" style={{ overflowY: "auto" }}>
                      <p>{careerRecommendation}</p>
                    </Card.Body>
                  </Card>
                </Col>

                <Col md={6} lg={3} className="mb-4">
                  <Card className="shadow border-0 h-100">
                    <Card.Header className="bg-warning text-white py-3">
                      <h4 className="mb-0 fw-bold">Skill Gap</h4>
                    </Card.Header>
                    <Card.Body className="p-4">
                      <p><strong>Current Skills:</strong> {skillGap.currentSkills.map(s => `${s.name} (${s.proficiency}%)`).join(", ")}</p>
                      <p><strong>Required Skills:</strong> {skillGap.requiredSkills.map(s => `${s.name} (${s.proficiency}%)`).join(", ")}</p>
                      {chartData.length > 1 && (
                        <Chart
                          chartType="BarChart"
                          width="100%"
                          height="300px"
                          data={chartData}
                          options={{
                            title: "Skill Gap Analysis",
                            chartArea: { width: "60%" },
                            hAxis: { title: "Skill", titleTextStyle: { color: "#333" } },
                            vAxis: { title: "Proficiency (%)", minValue: 0, maxValue: 100 },
                            legend: { position: "top", maxLines: 2 },
                            colors: ["#4285f4", "#db4437"],
                            bar: { groupWidth: "75%" },
                          }}
                        />
                      )}
                    </Card.Body>
                  </Card>
                </Col>

                <Col md={6} lg={3} className="mb-4">
                  <Card className="shadow border-0 h-100">
                    <Card.Header className="bg-info text-white py-3">
                      <h4 className="mb-0 fw-bold">Roadmap</h4>
                    </Card.Header>
                    <Card.Body className="p-4" style={{ overflowY: "auto" }}>
                      <ol>
                        {roadmap.map((step, index) => (
                          <li key={index}>
                            <strong>{step.step}</strong>: {step.description} ({step.duration})
                            <ul>
                              {step.tasks.map((task, i) => (
                                <li key={i}>{task}</li>
                              ))}
                            </ul>
                          </li>
                        ))}
                      </ol>
                    </Card.Body>
                  </Card>
                </Col>

                <Col md={6} lg={3} className="mb-4">
                  <Card className="shadow border-0 h-100">
                    <Card.Header className="bg-primary text-white py-3">
                      <h4 className="mb-0 fw-bold">Learning Resources</h4>
                    </Card.Header>
                    <Card.Body className="p-4" style={{ overflowY: "auto" }}>
                      <ul>
                        {Object.entries(learningResources).map(([skill, url], index) => (
                          <li key={index}>
                            <strong>{skill}:</strong> <a href={url} target="_blank" rel="noopener noreferrer">{url}</a>
                          </li>
                        ))}
                      </ul>
                      <div className="text-center mt-4">
                        <Button variant="outline-primary" onClick={downloadPDF}>
                          <i className="fas fa-download me-2"></i>Download Report
                        </Button>
                      </div>
                    </Card.Body>
                  </Card>
                </Col>
              </Row>
            )}
          </>
        )}
      </Container>
    </div>
  );
};

export default Dashboard;
