import React, { useState, useEffect } from "react";
import { Container, Button, Form, Spinner, Card, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { jsPDF } from "jspdf";
import Select from "react-select";
import { Chart } from "react-google-charts";

const skillOptions = [
  { value: "JavaScript", label: "JavaScript" },
  { value: "Python", label: "Python" },
  { value: "Project Management", label: "Project Management" },
  { value: "React", label: "React" },
  { value: "Java", label: "Java" },
  { value: "SQL", label: "SQL" },
];

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    skills: "",
    educationLevel: "",
    degree: "",
    experience: "",
    jobRole: "",
    interests: "",
    certifications: "",
    location: "",
  });
  const [loading, setLoading] = useState(false);
  const [recommendations, setRecommendations] = useState(null);

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");
    if (!token || !userData) {
      alert("Please log in first!");
      navigate("/");
    }
    setUser(userData);
    setFormData({
      firstName: userData.firstName,
      lastName: userData.lastName,
      skills: "",
      educationLevel: "",
      degree: "",
      experience: "",
      jobRole: "",
      interests: "",
      certifications: "",
      location: "",
    });
  }, [navigate]);

  const handleProfileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.put(
        "http://localhost:5000/auth/edit-profile",
        formData,
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      localStorage.setItem("user", JSON.stringify(response.data.user));
      setUser(response.data.user);
      setIsEditing(false);
    } catch (error) {
      alert("Error updating profile.");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setRecommendations(null);
    try {
      const response = await axios.post(
        "http://localhost:5000/career/career-recommendation",
        formData,
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      console.log("API Response:", response.data.recommendations); // Debugging
      setRecommendations(response.data.recommendations);
    } catch (error) {
      console.error("Error fetching recommendations:", error);
      setRecommendations({ error: "Error fetching career recommendations." });
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/");
  };

  const downloadPDF = () => {
    const doc = new jsPDF("p", "mm", "a4");
    doc.setFont("helvetica", "bold");
    doc.setFontSize(22);
    doc.text("PathToGrow", 14, 20);
    doc.setFontSize(18);
    doc.text("Career Recommendations", 14, 30);
    doc.setLineWidth(0.5);
    doc.line(14, 32, 200, 32);
    doc.setFont("helvetica", "normal");
    doc.setFontSize(12);
    let yOffset = 40;

    if (recommendations) {
      doc.text("Career Recommendation:", 14, yOffset);
      yOffset += 10;
      doc.text(recommendations.careerRecommendation || "N/A", 14, yOffset, { maxWidth: 180 });
      yOffset += 20;

      doc.text("Skill Gap:", 14, yOffset);
      yOffset += 10;
      doc.text(JSON.stringify(recommendations.skillGap || "N/A"), 14, yOffset);
      yOffset += 20;

      doc.text("Roadmap:", 14, yOffset);
      yOffset += 10;
      recommendations.roadmap?.forEach((step) => {
        doc.text(`Step ${step.step}: ${step.description} (${step.duration})`, 14, yOffset);
        yOffset += 10;
        step.tasks.forEach((task) => {
          doc.text(`- ${task}`, 20, yOffset);
          yOffset += 10;
        });
        if (yOffset > 270) {
          doc.addPage();
          yOffset = 20;
        }
      });

      yOffset += 10;
      doc.text("Learning Resources:", 14, yOffset);
      yOffset += 10;
      for (const [skill, link] of Object.entries(recommendations.learningResources || {})) {
        doc.text(`${skill}: ${link}`, 14, yOffset, { maxWidth: 180 });
        yOffset += 10;
        if (yOffset > 270) {
          doc.addPage();
          yOffset = 20;
        }
      }
    }
    doc.save("career_recommendations.pdf");
  };

  const getSkillGapChartData = () => {
    if (!recommendations || !recommendations.skillGap) return [["Skill", "Current", "Required"]];
    const { currentSkills, requiredSkills } = recommendations.skillGap;
    const allSkills = [...new Set([...currentSkills.map(s => s.skill || s.name), ...requiredSkills.map(s => s.skill || s.name)])];
    return [
      ["Skill", "Current Proficiency", "Required Proficiency"],
      ...allSkills.map(skill => {
        const current = currentSkills.find(s => (s.skill || s.name) === skill)?.proficiency || 0;
        const required = requiredSkills.find(s => (s.skill || s.name) === skill)?.proficiency || 0;
        return [skill, current, required];
      }),
    ];
  };

  return (
    <div className="dashboard-page py-5" style={{ backgroundColor: "#f8f9fa" }}>
      <Container>
        {/* User Profile Card */}
        <Card className="shadow mb-5 border-0 rounded">
          <Card.Body className="p-4">
            <Row className="align-items-center">
              <Col md={2} className="text-center">
                <div className="avatar-placeholder bg-primary rounded-circle d-flex align-items-center justify-content-center mx-auto" style={{ width: "80px", height: "80px" }}>
                  <h2 className="text-white m-0">{user?.firstName?.[0]}{user?.lastName?.[0]}</h2>
                </div>
              </Col>
              <Col md={8}>
                <h3 className="fw-bold mb-1">Welcome, {user?.firstName} {user?.lastName}!</h3>
                <p className="text-muted mb-0"><i className="fas fa-envelope me-2"></i>{user?.email}</p>
              </Col>
              <Col md={2} className="text-end">
                <Button
                  variant="outline-primary"
                  className="rounded-pill me-2"
                  onClick={() => setIsEditing(true)}
                >
                  <i className="fas fa-edit me-2"></i>Edit Profile
                </Button>
                <Button
                  variant="outline-danger"
                  className="rounded-pill"
                  onClick={handleLogout}
                >
                  <i className="fas fa-sign-out-alt me-2"></i>Logout
                </Button>
              </Col>
            </Row>
          </Card.Body>
        </Card>

        {/* Profile Edit Form */}
        {isEditing ? (
          <Card className="shadow mb-5 border-0">
            <Card.Body className="p-4">
              <Form onSubmit={handleProfileSubmit}>
                <h4 className="fw-bold mb-4 border-bottom pb-3">Edit Your Profile</h4>
                <Row>
                  <Col md={6}>
                    <Form.Group controlId="firstName" className="mb-3">
                      <Form.Label>First Name</Form.Label>
                      <Form.Control
                        type="text"
                        name="firstName"
                        value={formData.firstName}
                        onChange={handleProfileChange}
                        className="py-2"
                        required
                      />
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group controlId="lastName" className="mb-3">
                      <Form.Label>Last Name</Form.Label>
                      <Form.Control
                        type="text"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleProfileChange}
                        className="py-2"
                        required
                      />
                    </Form.Group>
                  </Col>
                </Row>
                <div className="d-flex justify-content-end mt-3">
                  <Button
                    variant="secondary"
                    className="me-2"
                    onClick={() => setIsEditing(false)}
                  >
                    Cancel
                  </Button>
                  <Button variant="primary" type="submit" disabled={loading}>
                    {loading ? <Spinner animation="border" size="sm" /> : "Save Changes"}
                  </Button>
                </div>
              </Form>
            </Card.Body>
          </Card>
        ) : (
          <>
            <div className="text-center mb-4">
              <h2 className="fw-bold">Your Skill Gap Analysis & Career Recommendations</h2>
              <p className="text-muted">Fill in the details below to receive personalized career guidance</p>
            </div>

            <Card className="shadow border-0 mb-5">
              <Card.Body className="p-4">
                <Form onSubmit={handleSubmit}>
                  <Row>
                    <Col md={6}>
                      <Form.Group controlId="skills" className="mb-3">
                        <Form.Label className="fw-bold">Your Skills</Form.Label>
                        <Select
                          isMulti
                          name="skills"
                          options={skillOptions}
                          value={formData.skills ? formData.skills.split(", ").map(skill => ({ value: skill, label: skill })) : []}
                          onChange={(selectedOptions) =>
                            setFormData({
                              ...formData,
                              skills: selectedOptions.map(option => option.value).join(", "),
                            })
                          }
                          placeholder="Select or type your skills..."
                        />
                      </Form.Group>

                      <Form.Group controlId="educationLevel" className="mb-3">
                        <Form.Label className="fw-bold">Education Level</Form.Label>
                        <Form.Select
                          name="educationLevel"
                          value={formData.educationLevel || ""}
                          onChange={handleChange}
                          className="py-2"
                        >
                          <option value="">Select your education level</option>
                          <option value="High School">High School</option>
                          <option value="Associate's Degree">Associate's Degree</option>
                          <option value="Bachelor's Degree">Bachelor's Degree</option>
                          <option value="Master's Degree">Master's Degree</option>
                          <option value="PhD">PhD</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group controlId="degree" className="mb-3">
                        <Form.Label className="fw-bold">Degree/Field of Study</Form.Label>
                        <Form.Control
                          type="text"
                          name="degree"
                          value={formData.degree || ""}
                          onChange={handleChange}
                          placeholder="Computer Science, Business, etc."
                          className="py-2"
                        />
                      </Form.Group>

                      <Form.Group controlId="interests" className="mb-3">
                        <Form.Label className="fw-bold">Interests</Form.Label>
                        <Form.Control
                          type="text"
                          name="interests"
                          value={formData.interests || ""}
                          onChange={handleChange}
                          placeholder="AI, Design, Finance, etc."
                          className="py-2"
                        />
                      </Form.Group>
                    </Col>

                    <Col md={6}>
                      <Form.Group controlId="experience" className="mb-3">
                        <Form.Label className="fw-bold">Years of Experience</Form.Label>
                        <Form.Select
                          name="experience"
                          value={formData.experience || ""}
                          onChange={handleChange}
                          className="py-2"
                        >
                          <option value="">Select years of experience</option>
                          <option value="0-1">0-1 years</option>
                          <option value="1-3">1-3 years</option>
                          <option value="3-5">3-5 years</option>
                          <option value="5-10">5-10 years</option>
                          <option value="10+">10+ years</option>
                        </Form.Select>
                      </Form.Group>

                      <Form.Group controlId="jobRole" className="mb-3">
                        <Form.Label className="fw-bold">Desired Job Role</Form.Label>
                        <Form.Control
                          type="text"
                          name="jobRole"
                          value={formData.jobRole || ""}
                          onChange={handleChange}
                          placeholder="Data Scientist, UX Designer, etc."
                          className="py-2"
                        />
                      </Form.Group>

                      <Form.Group controlId="certifications" className="mb-3">
                        <Form.Label className="fw-bold">Certifications</Form.Label>
                        <Form.Control
                          type="text"
                          name="certifications"
                          value={formData.certifications || ""}
                          onChange={handleChange}
                          placeholder="AWS, PMP, etc."
                          className="py-2"
                        />
                      </Form.Group>

                      <Form.Group controlId="location" className="mb-3">
                        <Form.Label className="fw-bold">Location Preference</Form.Label>
                        <Form.Control
                          type="text"
                          name="location"
                          value={formData.location || ""}
                          onChange={handleChange}
                          placeholder="Remote, New York, etc."
                          className="py-2"
                        />
                      </Form.Group>

                      <div className="d-grid mt-4">
                        <Button
                          variant="primary"
                          type="submit"
                          size="lg"
                          className="py-2"
                          disabled={loading}
                        >
                          {loading ? (
                            <Spinner animation="border" size="sm" className="me-2" />
                          ) : (
                            <i className="fas fa-search me-2"></i>
                          )}
                          Analyze My Skills
                        </Button>
                      </div>
                    </Col>
                  </Row>
                </Form>
              </Card.Body>
            </Card>

            {/* Display Career Recommendations in Separate Boxes */}
            {recommendations && !loading && (
              <Row>
                <Col md={6} className="mb-4">
                  <Card className="shadow border-0 animation-fade-in">
                    <Card.Header className="bg-primary text-white py-3">
                      <h4 className="mb-0 fw-bold">
                        <i className="fas fa-briefcase me-2"></i>
                        Career Recommendation
                      </h4>
                    </Card.Header>
                    <Card.Body className="p-4">
                      {recommendations.careerRecommendation ? (
                        <p>{recommendations.careerRecommendation}</p>
                      ) : (
                        <p>N/A</p>
                      )}
                    </Card.Body>
                  </Card>
                </Col>

                <Col md={6} className="mb-4">
                  <Card className="shadow border-0 animation-fade-in">
                    <Card.Header className="bg-success text-white py-3">
                      <h4 className="mb-0 fw-bold">
                        <i className="fas fa-chart-bar me-2"></i>
                        Skill Gap Analysis
                      </h4>
                    </Card.Header>
                    <Card.Body className="p-4">
                      {recommendations.skillGap ? (
                        <Chart
                          chartType="BarChart"
                          width="100%"
                          height="300px"
                          data={getSkillGapChartData()}
                          options={{
                            title: "Skill Proficiency: Current vs Required",
                            hAxis: { title: "Skills" },
                            vAxis: { title: "Proficiency (0-100)" },
                            legend: { position: "top" },
                            colors: ["#007bff", "#dc3545"],
                          }}
                        />
                      ) : (
                        <p>No skill gap data available.</p>
                      )}
                    </Card.Body>
                  </Card>
                </Col>

                <Col md={6} className="mb-4">
                  <Card className="shadow border-0 animation-fade-in">
                    <Card.Header className="bg-info text-white py-3">
                      <h4 className="mb-0 fw-bold">
                        <i className="fas fa-road me-2"></i>
                        Roadmap
                      </h4>
                    </Card.Header>
                    <Card.Body className="p-4">
                      {recommendations.roadmap && recommendations.roadmap.length > 0 ? (
                        <ul className="list-group">
                          {recommendations.roadmap.map((step, index) => (
                            <li key={index} className="list-group-item">
                              <strong>Step {step.step}:</strong> {step.description} ({step.duration})
                              <ul>
                                {step.tasks.map((task, taskIndex) => (
                                  <li key={taskIndex}>{task}</li>
                                ))}
                              </ul>
                            </li>
                          ))}
                        </ul>
                      ) : (
                        <p>No roadmap available.</p>
                      )}
                    </Card.Body>
                  </Card>
                </Col>

                <Col md={6} className="mb-4">
                  <Card className="shadow border-0 animation-fade-in">
                    <Card.Header className="bg-warning text-white py-3">
                      <h4 className="mb-0 fw-bold">
                        <i className="fas fa-book me-2"></i>
                        Learning Resources
                      </h4>
                    </Card.Header>
                    <Card.Body className="p-4">
                      {recommendations.learningResources && Object.keys(recommendations.learningResources).length > 0 ? (
                        <ul>
                          {Object.entries(recommendations.learningResources).map(([skill, link], index) => (
                            <li key={index}>
                              <strong>{skill}:</strong>{" "}
                              <a href={link} target="_blank" rel="noopener noreferrer">
                                {link}
                              </a>
                            </li>
                          ))}
                        </ul>
                      ) : (
                        <p>No resources available.</p>
                      )}
                    </Card.Body>
                  </Card>
                </Col>

                <Col md={12} className="text-center">
                  <Button variant="outline-primary" onClick={downloadPDF}>
                    <i className="fas fa-download me-2"></i>
                    Download Report
                  </Button>
                </Col>
              </Row>
            )}
          </>
        )}
      </Container>
    </div>
  );
};

export default Dashboard;
