import React, { useState, useEffect } from "react";
import { Container, Button, Form, Spinner, Card, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Chart } from "react-google-charts";
import { jsPDF } from "jspdf";

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [loading, setLoading] = useState(false);
  const [recommendations, setRecommendations] = useState({});
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    skills: "",
    educationLevel: "",
    degree: "",
    experience: "",
    jobRole: "",
    interests: "",
    certifications: "",
    location: ""
  });

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("user"));
    if (!userData) {
      alert("Please log in first!");
      navigate("/");
    }
    setUser(userData);
    setFormData(prev => ({ ...prev, firstName: userData.firstName, lastName: userData.lastName }));
  }, [navigate]);

  const handleProfileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      const response = await axios.put(
        "http://localhost:5000/auth/edit-profile",
        formData,
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );

      localStorage.setItem("user", JSON.stringify(response.data.user));
      setUser(response.data.user);
      setIsEditing(false);
    } catch (error) {
      alert("Error updating profile.");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setRecommendations({});

    try {
      const response = await axios.post(
        "http://localhost:5000/career/career-recommendation",
        formData,
        { headers: { Authorization: `Bearer ${localStorage.getItem("token")}` } }
      );
      setRecommendations(response.data.recommendations);
    } catch (error) {
      setRecommendations({ careerRecommendation: "Error fetching recommendations." });
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/");
  };

  return (
    <div className="dashboard-page py-5" style={{ backgroundColor: "#f8f9fa" }}>
      <Container>
        {/* User Profile Card */}
        <Card className="shadow mb-5 border-0 rounded">
          <Card.Body className="p-4">
            <Row className="align-items-center">
              <Col md={2} className="text-center">
                <div className="avatar-placeholder bg-primary rounded-circle d-flex align-items-center justify-content-center mx-auto" style={{ width: "80px", height: "80px" }}>
                  <h2 className="text-white m-0">
                    {user?.firstName?.[0]}{user?.lastName?.[0]}
                  </h2>
                </div>
              </Col>
              <Col md={8}>
                <h3 className="fw-bold mb-1">Welcome, {user?.firstName} {user?.lastName}!</h3>
                <p className="text-muted mb-0">
                  <i className="fas fa-envelope me-2"></i>{user?.email}
                </p>
              </Col>
              <Col md={2} className="text-end">
                <Button variant="outline-danger" className="rounded-pill" onClick={handleLogout}>
                  <i className="fas fa-sign-out-alt me-2"></i>Logout
                </Button>
              </Col>
            </Row>
          </Card.Body>
        </Card>

        {/* Profile Edit Form */}
        {isEditing ? (
          <Card className="shadow mb-5 border-0">
            <Card.Body className="p-4">
              <Form onSubmit={handleProfileSubmit}>
                <h4 className="fw-bold mb-4 border-bottom pb-3">Edit Your Profile</h4>
                <Row>
                  <Col md={6}>
                    <Form.Group controlId="firstName" className="mb-3">
                      <Form.Label>First Name</Form.Label>
                      <Form.Control type="text" name="firstName" value={formData.firstName} onChange={handleProfileChange} required />
                    </Form.Group>
                  </Col>
                  <Col md={6}>
                    <Form.Group controlId="lastName" className="mb-3">
                      <Form.Label>Last Name</Form.Label>
                      <Form.Control type="text" name="lastName" value={formData.lastName} onChange={handleProfileChange} required />
                    </Form.Group>
                  </Col>
                </Row>
                <div className="d-flex justify-content-end mt-3">
                  <Button variant="secondary" className="me-2" onClick={() => setIsEditing(false)}>Cancel</Button>
                  <Button variant="primary" type="submit" disabled={loading}>
                    {loading ? <Spinner animation="border" size="sm" /> : "Save Changes"}
                  </Button>
                </div>
              </Form>
            </Card.Body>
          </Card>
        ) : (
          <>
            {/* Form for Career Analysis */}
            <Card className="shadow border-0 mb-5">
              <Card.Body>
                <h2 className="fw-bold text-center mb-4">Career Analysis</h2>
                <Form onSubmit={handleSubmit}>
                  <Row>
                    <Col md={6}>
                      <Form.Group controlId="skills" className="mb-3">
                        <Form.Label>Skills</Form.Label>
                        <Form.Control type="text" name="skills" onChange={handleChange} required />
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="jobRole" className="mb-3">
                        <Form.Label>Job Role</Form.Label>
                        <Form.Control type="text" name="jobRole" onChange={handleChange} required />
                      </Form.Group>
                    </Col>
                  </Row>
                  <Button type="submit" disabled={loading}>{loading ? <Spinner size="sm" /> : "Analyze"}</Button>
                </Form>
              </Card.Body>
            </Card>

            {/* Display AI-generated Results */}
            {recommendations.careerRecommendation && <Card><Card.Body><h4>Career Recommendation</h4><p>{recommendations.careerRecommendation}</p></Card.Body></Card>}
            
            {recommendations.skillGap && (
              <Card className="mt-3">
                <Card.Body>
                  <h4>Skill Gap Analysis</h4>
                  <Chart
                    chartType="BarChart"
                    width="100%"
                    height="300px"
                    data={[
                      ["Skill", "Current Level", "Required Level"],
                      ...recommendations.skillGap.currentSkills.map(skill => [
                        skill.name, skill.proficiency, recommendations.skillGap.requiredSkills.find(r => r.name === skill.name)?.proficiency || 100
                      ])
                    ]}
                  />
                </Card.Body>
              </Card>
            )}

            {recommendations.roadmap && <Card><Card.Body><h4>Roadmap</h4><ul>{recommendations.roadmap.map((step, index) => <li key={index}>{step.description}</li>)}</ul></Card.Body></Card>}

            {recommendations.learningResources && <Card><Card.Body><h4>Learning Resources</h4><ul>{Object.entries(recommendations.learningResources).map(([skill, url], index) => (<li key={index}><a href={url} target="_blank" rel="noopener noreferrer">{skill}</a></li>))}</ul></Card.Body></Card>}
          </>
        )}
      </Container>
    </div>
  );
};

export default Dashboard;
