import React, { useState, useEffect } from "react";
import { Container, Button, Form, Spinner, Alert } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
  });
  const [loading, setLoading] = useState(false);
  const [recommendations, setRecommendations] = useState("");

  useEffect(() => {
    // Get user data from localStorage
    const userData = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");

    // If no token or user data, redirect to login page
    if (!token || !userData) {
      alert("Please log in first!");
      navigate("/");  // Redirect to login page
    }

    // Set the user data in state
    setUser(userData);
    setFormData({
      firstName: userData.firstName,
      lastName: userData.lastName,
    });
  }, [navigate]);

  const handleProfileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      const response = await axios.put(
        "http://localhost:5000/auth/edit-profile", // Backend route to update profile
        formData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      
      // Update user data in localStorage
      localStorage.setItem("user", JSON.stringify(response.data.user));

      // Set the updated user data in the state
      setUser(response.data.user);
      setIsEditing(false);  // Close the profile edit form
    } catch (error) {
      alert("Error updating profile.");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setRecommendations(""); // Clear previous recommendations
  
    // Log the data being sent to the backend
    console.log("Sending data to API:", formData);
  
    try {
      const response = await axios.post(
        "http://localhost:5000/career/career-recommendation",
        formData,
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`, // Ensure token is valid
          },
        }
      );
  
      // Debugging: Log response from the API
      console.log("Response received:", response.data);
  
      if (response.data && response.data.recommendations) {
        setRecommendations(response.data.recommendations);
      } else {
        setRecommendations("No recommendations available.");
      }
    } catch (error) {
      console.error("Error fetching recommendations:", error.response?.data || error.message);
      setRecommendations("Error fetching career recommendations. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container>
      <div className="profile-info">
        <h3>Welcome, {user?.firstName} {user?.lastName}!</h3>
        <p>Email: {user?.email}</p>
        <Button variant="outline-primary" onClick={() => setIsEditing(true)}>
          Edit Profile
        </Button>
      </div>

      {isEditing ? (
        <Form onSubmit={handleProfileSubmit}>
          <h4>Edit Profile</h4>
          <Form.Group controlId="firstName">
            <Form.Label>First Name</Form.Label>
            <Form.Control
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleProfileChange}
              required
            />
          </Form.Group>
          
          <Form.Group controlId="lastName">
            <Form.Label>Last Name</Form.Label>
            <Form.Control
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleProfileChange}
              required
            />
          </Form.Group>

          <Button variant="primary" type="submit" disabled={loading}>
            {loading ? <Spinner animation="border" size="sm" /> : "Save Changes"}
          </Button>
        </Form>
      ) : (
        <>
          <h2>Your Skill Gap Analysis & Career Recommendations</h2>
          {/* Form for user input */}
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="skills">
              <Form.Label>Your Skills</Form.Label>
              <Form.Control
                type="text"
                name="skills"
                value={formData.skills}
                onChange={handleChange}
                placeholder="Enter your skills separated by commas (e.g., JavaScript, Project Management)"
              />
            </Form.Group>

            <Form.Group controlId="educationLevel">
              <Form.Label>Education Level</Form.Label>
              <Form.Control
                as="select"
                name="educationLevel"
                value={formData.educationLevel}
                onChange={handleChange}
              >
                <option value="">Select your education level</option>
                <option value="High School">High School</option>
                <option value="Associate's Degree">Associate's Degree</option>
                <option value="Bachelor's Degree">Bachelor's Degree</option>
                <option value="Master's Degree">Master's Degree</option>
                <option value="PhD">PhD</option>
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="degree">
              <Form.Label>Degree/Field of Study</Form.Label>
              <Form.Control
                type="text"
                name="degree"
                value={formData.degree}
                onChange={handleChange}
                placeholder="Enter your degree or field of study"
              />
            </Form.Group>

            <Form.Group controlId="experience">
              <Form.Label>Years of Experience</Form.Label>
              <Form.Control
                as="select"
                name="experience"
                value={formData.experience}
                onChange={handleChange}
              >
                <option value="">Select years of experience</option>
                <option value="0-1">0-1 years</option>
                <option value="1-3">1-3 years</option>
                <option value="3-5">3-5 years</option>
                <option value="5-10">5-10 years</option>
                <option value="10+">10+ years</option>
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="jobRole">
              <Form.Label>Desired Job Role</Form.Label>
              <Form.Control
                type="text"
                name="jobRole"
                value={formData.jobRole}
                onChange={handleChange}
                placeholder="Enter your desired job role (e.g., Data Scientist, UX Designer)"
              />
            </Form.Group>

            <Button variant="primary" type="submit" className="mt-3">
              Analyze My Skills
            </Button>
          </Form>

          {/* Display Career Recommendations */}
          {loading && <Spinner animation="border" variant="primary" />}

          {recommendations && !loading && (
            <div className="mt-3">
              <h4>Career Recommendations</h4>
              <p>{recommendations}</p>
            </div>
          )}
        </>
      )}
    </Container>
  );
};

export default Dashboard;
