import React, { useState, useEffect } from "react";
import { Container, Button, Form, Spinner, Card, Row, Col } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { jsPDF } from "jspdf"; // Import jsPDF
import { Chart } from "react-google-charts"; // Import Google Charts

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    skills: "",
    educationLevel: "",
    degree: "",
    experience: "",
    jobRole: "",
  });
  const [loading, setLoading] = useState(false);
  const [recommendations, setRecommendations] = useState(null);
  const [chartData, setChartData] = useState([]);

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem("user"));
    const token = localStorage.getItem("token");
    if (!token || !userData) {
      alert("Please log in first!");
      navigate("/");
    }
    setUser(userData);
    setFormData({
      firstName: userData.firstName,
      lastName: userData.lastName,
    });
  }, [navigate]);

  const handleProfileChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.put(
        "http://localhost:5000/auth/edit-profile",
        formData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      localStorage.setItem("user", JSON.stringify(response.data.user));
      setUser(response.data.user);
      setIsEditing(false);
    } catch (error) {
      alert("Error updating profile.");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setRecommendations(null); // Clear previous recommendations
    try {
      const response = await axios.post(
        "http://localhost:5000/career/career-recommendation",
        formData,
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      setRecommendations(response.data.recommendations);

      // Prepare chart data for skill gap visualization
      const chartDataArray = [["Skill", "Current Proficiency", "Required Proficiency"]];
      response.data.recommendations.skillGap.currentSkills.forEach((currentSkill) => {
        const requiredSkill = response.data.recommendations.skillGap.requiredSkills.find(
          (skill) => skill.name === currentSkill.name
        );
        if (requiredSkill) {
          chartDataArray.push([currentSkill.name, currentSkill.proficiency, requiredSkill.proficiency]);
        }
      });
      setChartData(chartDataArray);
    } catch (error) {
      setRecommendations("Error fetching career recommendations.");
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/");
  };

  const downloadPDF = () => {
    const doc = new jsPDF("p", "mm", "a4");
    doc.setFont("helvetica", "bold");
    doc.setFontSize(22);
    doc.text("PathToGrow", 14, 20);
    doc.setFontSize(18);
    doc.setFont("helvetica", "normal");
    doc.text("Career Recommendations", 14, 30);
    doc.setLineWidth(0.5);
    doc.line(14, 32, 200, 32);
    doc.setFont("helvetica", "normal");
    doc.setFontSize(12);
    const recommendationsArray = recommendations.careerRecommendation.split('\n');
    let yOffset = 40;
    recommendationsArray.forEach((paragraph) => {
      doc.text(paragraph, 14, yOffset);
      yOffset += 10;
      if (yOffset > 270) {
        doc.addPage();
        yOffset = 20;
      }
    });
    doc.save("career_recommendations.pdf");
  };

  return (
    <div className="dashboard-page py-5" style={{ backgroundColor: "#f8f9fa" }}>
      <Container>
        {/* User Profile Card */}
        <Card className="shadow mb-5 border-0 rounded">
          <Card.Body className="p-4">
            <Row className="align-items-center">
              <Col md={2} className="text-center">
                <div
                  className="avatar-placeholder bg-primary rounded-circle d-flex align-items-center justify-content-center mx-auto"
                  style={{ width: "80px", height: "80px" }}
                >
                  <h2 className="text-white m-0">
                    {user?.firstName?.[0]}{user?.lastName?.[0]}
                  </h2>
                </div>
              </Col>
              <Col md={8}>
                <h3 className="fw-bold mb-1">Welcome, {user?.firstName} {user?.lastName}!</h3>
                <p className="text-muted mb-0">
                  <i className="fas fa-envelope me-2"></i>{user?.email}
                </p>
              </Col>
              <Col md={2} className="text-end">
                <Button
                  variant="outline-danger"
                  className="rounded-pill"
                  onClick={handleLogout}
                >
                  <i className="fas fa-sign-out-alt me-2"></i>Logout
                </Button>
              </Col>
            </Row>
          </Card.Body>
        </Card>

        {/* Career Recommendation Submission Form */}
        {!isEditing && (
          <>
            <div className="text-center mb-4">
              <h2 className="fw-bold">Your Skill Gap Analysis & Career Recommendations</h2>
              <p className="text-muted">Fill in the details below to receive personalized career guidance</p>
            </div>
            <Card className="shadow border-0 mb-5">
              <Card.Body className="p-4">
                <Form onSubmit={handleSubmit}>
                  <Row>
                    <Col md={6}>
                      <Form.Group controlId="skills" className="mb-3">
                        <Form.Label className="fw-bold">Your Skills</Form.Label>
                        <Form.Control
                          type="text"
                          name="skills"
                          value={formData.skills || ""}
                          onChange={handleChange}
                          placeholder="JavaScript, Project Management, etc."
                          className="py-2"
                        />
                      </Form.Group>
                      <Form.Group controlId="educationLevel" className="mb-3">
                        <Form.Label className="fw-bold">Education Level</Form.Label>
                        <Form.Select
                          name="educationLevel"
                          value={formData.educationLevel || ""}
                          onChange={handleChange}
                          className="py-2"
                        >
                          <option value="">Select your education level</option>
                          <option value="High School">High School</option>
                          <option value="Associate's Degree">Associate's Degree</option>
                          <option value="Bachelor's Degree">Bachelor's Degree</option>
                          <option value="Master's Degree">Master's Degree</option>
                          <option value="PhD">PhD</option>
                        </Form.Select>
                      </Form.Group>
                      <Form.Group controlId="degree" className="mb-3">
                        <Form.Label className="fw-bold">Degree/Field of Study</Form.Label>
                        <Form.Control
                          type="text"
                          name="degree"
                          value={formData.degree || ""}
                          onChange={handleChange}
                          placeholder="Computer Science, Business, etc."
                          className="py-2"
                        />
                      </Form.Group>
                    </Col>
                    <Col md={6}>
                      <Form.Group controlId="experience" className="mb-3">
                        <Form.Label className="fw-bold">Years of Experience</Form.Label>
                        <Form.Select
                          name="experience"
                          value={formData.experience || ""}
                          onChange={handleChange}
                          className="py-2"
                        >
                          <option value="">Select years of experience</option>
                          <option value="0-1">0-1 years</option>
                          <option value="1-3">1-3 years</option>
                          <option value="3-5">3-5 years</option>
                          <option value="5-10">5-10 years</option>
                          <option value="10+">10+ years</option>
                        </Form.Select>
                      </Form.Group>
                      <Form.Group controlId="jobRole" className="mb-3">
                        <Form.Label className="fw-bold">Desired Job Role</Form.Label>
                        <Form.Control
                          type="text"
                          name="jobRole"
                          value={formData.jobRole || ""}
                          onChange={handleChange}
                          placeholder="Data Scientist, UX Designer, etc."
                          className="py-2"
                        />
                      </Form.Group>
                      <div className="d-grid mt-4">
                        <Button
                          variant="primary"
                          type="submit"
                          size="lg"
                          className="py-2"
                          disabled={loading}
                        >
                          {loading ? (
                            <Spinner animation="border" size="sm" className="me-2" />
                          ) : (
                            <i className="fas fa-search me-2"></i>
                          )}
                          Analyze My Skills
                        </Button>
                      </div>
                    </Col>
                  </Row>
                </Form>
              </Card.Body>
            </Card>
          </>
        )}

        {/* Display Career Recommendations */}
        {recommendations && !loading && (
          <>
            <Row className="mb-5">
              <Col md={6}>
                <Card className="shadow border-0">
                  <Card.Header className="bg-primary text-white py-3">
                    <h4 className="mb-0 fw-bold">Career Recommendation</h4>
                  </Card.Header>
                  <Card.Body className="p-4">
                    <p>{recommendations.careerRecommendation}</p>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={6}>
                <Card className="shadow border-0">
                  <Card.Header className="bg-primary text-white py-3">
                    <h4 className="mb-0 fw-bold">Skill Gap Analysis</h4>
                  </Card.Header>
                  <Card.Body className="p-4">
                    <Chart
                      chartType="BarChart"
                      data={chartData}
                      options={{
                        title: "Skill Gap Analysis",
                        chartArea: { width: "70%" },
                        hAxis: { title: "Proficiency", minValue: 0, maxValue: 100 },
                        vAxis: { title: "Skills" },
                      }}
                      width="100%"
                      height="400px"
                    />
                  </Card.Body>
                </Card>
              </Col>
            </Row>

            <Row className="mb-5">
              <Col md={6}>
                <Card className="shadow border-0">
                  <Card.Header className="bg-primary text-white py-3">
                    <h4 className="mb-0 fw-bold">Roadmap</h4>
                  </Card.Header>
                  <Card.Body className="p-4">
                    <ul>
                      {recommendations.roadmap.map((step, index) => (
                        <li key={index}>
                          <strong>{step.step}</strong>: {step.description} ({step.duration})
                          <ul>
                            {step.tasks.map((task, taskIndex) => (
                              <li key={taskIndex}>{task}</li>
                            ))}
                          </ul>
                        </li>
                      ))}
                    </ul>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={6}>
                <Card className="shadow border-0">
                  <Card.Header className="bg-primary text-white py-3">
                    <h4 className="mb-0 fw-bold">Learning Resources</h4>
                  </Card.Header>
                  <Card.Body className="p-4">
                    <ul>
                      {Object.entries(recommendations.learningResources).map(([skill, resource], index) => (
                        <li key={index}>
                          <strong>{skill}</strong>:{" "}
                          <a href={resource} target="_blank" rel="noopener noreferrer">
                            {resource}
                          </a>
                        </li>
                      ))}
                    </ul>
                  </Card.Body>
                </Card>
              </Col>
            </Row>

            <div className="text-center">
              <Button variant="outline-primary" onClick={downloadPDF}>
                <i className="fas fa-download me-2"></i>Download Report
              </Button>
            </div>
          </>
        )}
      </Container>
    </div>
  );
};

export default Dashboard;
