import React, { useState } from "react";
import Modal from "../components/Modal"; // Import Modal component
import "bootstrap/dist/css/bootstrap.min.css";

const Home = () => {
  const [showModal, setShowModal] = useState(false); // State to show modal

  // Function to open the modal
  const openModal = () => {
    console.log("Opening Modal...");
    setShowModal(true);
  };

  // Function to close the modal
  const closeModal = () => {
    console.log("Closing Modal...");
    setShowModal(false);
  };

  return (
    <>
      {/* Navbar */}
      <nav className="navbar navbar-expand-lg navbar-light bg-white py-3">
        <div className="container">
          <a className="navbar-brand" href="#">
            PathToGrow
          </a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav me-auto justify-content-center">
              <li className="nav-item">
                <a className="nav-link" href="#">Home</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">About</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">Features</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">Contact</a>
              </li>
            </ul>
            {/* Button to trigger modal */}
            <button className="btn btn-dark rounded-pill px-4" onClick={openModal}>Sign Up</button>
          </div>
        </div>
      </nav>

      {/* Hero Section */}
      <div className="container">
        <section className="hero-section">
          <div className="hero-content">
            <h1 className="hero-title">Path To Grow</h1>
            <h2 className="hero-subtitle">Bridge Your Skill Gap with AI</h2>
            <p className="hero-description">Personalized career guidance and learning roadmaps tailored just for you</p>
            {/* Get Started Button */}
            <button className="btn get-started-btn" onClick={openModal}>
              Get Started
            </button>
          </div>
        </section>
      </div>

      {/* Modal Component */}
      <Modal show={showModal} onClose={closeModal} />
    </>
  );
};

export default Home;
